const { currentAge, retirementAge, currentBalance, annualSalary, contributionRate, employerMatchRate, employerMatchCap, annualReturnRate } = inputs;

if (currentAge >= retirementAge) {
  return {
    futureBalance: currentBalance,
    totalContributions: 0,
    totalEmployerMatch: 0,
    totalGrowth: 0
  };
}

const yearsToRetirement = retirementAge - currentAge;
const r = annualReturnRate / 100;

let balance = parseFloat(currentBalance);
let myTotalContributions = 0;
let employerTotalContributions = 0;

for (let i = 0; i < yearsToRetirement; i++) {
  const yourAnnualContribution = annualSalary * (contributionRate / 100);

  const matchedContributionPercent = Math.min(contributionRate, employerMatchCap);
  const employerAnnualContribution = annualSalary * (Math.min(matchedContributionPercent, employerMatchRate) / 100);

  myTotalContributions += yourAnnualContribution;
  employerTotalContributions += employerAnnualContribution;

  const totalAnnualDeposit = yourAnnualContribution + employerAnnualContribution;

  balance = (balance + totalAnnualDeposit) * (1 + r);
}

const totalGrowth = balance - currentBalance - myTotalContributions - employerTotalContributions;

return {
  futureBalance: balance,
  totalContributions: myTotalContributions,
  totalEmployerMatch: employerTotalContributions,
  totalGrowth: totalGrowth
};
