function calculate({ currentBalance, withdrawalAmount, currentAge, marginalTaxRate, stateTaxRate, annualGrowthRate }) {
    const federalPenaltyRate = 0.10;
    const retirementAge = 65;
    const earlyWithdrawalAgeThreshold = 59.5;

    currentBalance = parseFloat(currentBalance);
    withdrawalAmount = parseFloat(withdrawalAmount);
    currentAge = parseFloat(currentAge);
    marginalTaxRate = parseFloat(marginalTaxRate);
    stateTaxRate = parseFloat(stateTaxRate);
    annualGrowthRate = parseFloat(annualGrowthRate);

    // Input validation and capping
    if (withdrawalAmount > currentBalance) {
        withdrawalAmount = currentBalance; // Withdrawal cannot exceed current balance
    }
    if (withdrawalAmount < 0) withdrawalAmount = 0;
    if (currentAge < 0) currentAge = 0; // Age cannot be negative, though min is 18
    if (marginalTaxRate < 0) marginalTaxRate = 0;
    if (stateTaxRate < 0) stateTaxRate = 0;
    if (annualGrowthRate < 0) annualGrowthRate = 0;

    // 1. Federal 10% Early Withdrawal Penalty
    let federalPenalty = 0;
    if (currentAge < earlyWithdrawalAgeThreshold) {
        federalPenalty = withdrawalAmount * federalPenaltyRate;
    }

    // 2. Federal Income Tax
    const federalTax = withdrawalAmount * (marginalTaxRate / 100);

    // 3. State Income Tax
    const stateTax = withdrawalAmount * (stateTaxRate / 100);

    // 4. Total Penalties & Taxes
    const totalPenaltiesAndTaxes = federalPenalty + federalTax + stateTax;

    // 5. Net Amount Received
    const netWithdrawal = withdrawalAmount - totalPenaltiesAndTaxes;

    // 6. Estimated Future Growth Missed
    let missedGrowth = 0;
    if (currentAge < retirementAge) {
        const yearsToRetirement = retirementAge - currentAge;
        // Ensure yearsToRetirement is not negative if currentAge > retirementAge
        const adjustedYearsToRetirement = Math.max(0, yearsToRetirement);
        const futureValue = withdrawalAmount * Math.pow(1 + (annualGrowthRate / 100), adjustedYearsToRetirement);
        missedGrowth = futureValue - withdrawalAmount; // Only show the growth, not the principal
    } else {
        missedGrowth = 0; // No missed growth if already at or past retirement age
    }

    return {
        federalPenalty: federalPenalty,
        federalTax: federalTax,
        stateTax: stateTax,
        totalPenaltiesAndTaxes: totalPenaltiesAndTaxes,
        netWithdrawal: netWithdrawal,
        missedGrowth: missedGrowth
    };
}
