function calculate() {
  const inputs = arguments[0];

  const householdIncome = parseFloat(inputs.householdIncome);
  const householdSize = parseInt(inputs.householdSize);
  const fplForHouseholdSize = parseFloat(inputs.fplForHouseholdSize);
  const benchmarkPlanCost = parseFloat(inputs.benchmarkPlanCost);

  if (isNaN(householdIncome) || isNaN(householdSize) || isNaN(fplForHouseholdSize) || isNaN(benchmarkPlanCost) || householdSize <= 0 || fplForHouseholdSize <= 0) {
    return { aptc: 0, csrEligibility: 'N/A', fplPercentage: 0, postAptcPremium: 0 };
  }

  const fplPercentage = (householdIncome / fplForHouseholdSize) * 100;

  let requiredContributionPercentage;
  // Post-ARP/IRA enhanced subsidy structure (current for 2024-2025)
  if (fplPercentage < 150) {
    requiredContributionPercentage = 0;
  } else if (fplPercentage < 200) {
    // Linear interpolation from 0% at 150% FPL to 2% at 200% FPL
    requiredContributionPercentage = 0 + (2 - 0) * ((fplPercentage - 150) / 50);
  } else if (fplPercentage < 250) {
    // Linear interpolation from 2% at 200% FPL to 4% at 250% FPL
    requiredContributionPercentage = 2 + (4 - 2) * ((fplPercentage - 200) / 50);
  } else if (fplPercentage < 300) {
    // Linear interpolation from 4% at 250% FPL to 6% at 300% FPL
    requiredContributionPercentage = 4 + (6 - 4) * ((fplPercentage - 250) / 50);
  } else if (fplPercentage < 400) {
    // Linear interpolation from 6% at 300% FPL to 8.5% at 400% FPL
    requiredContributionPercentage = 6 + (8.5 - 6) * ((fplPercentage - 300) / 100);
  } else {
    // For incomes 400% FPL and above, capped at 8.5% (due to IRA enhancements)
    requiredContributionPercentage = 8.5;
  }

  const maxAffordablePremium = householdIncome * (requiredContributionPercentage / 100);

  const aptc = Math.max(0, benchmarkPlanCost - maxAffordablePremium);

  let csrEligibility;
  if (fplPercentage >= 100 && fplPercentage < 150) {
    csrEligibility = "Highest (94% Actuarial Value Silver plans)";
  } else if (fplPercentage >= 150 && fplPercentage < 200) {
    csrEligibility = "High (87% Actuarial Value Silver plans)";
  } else if (fplPercentage >= 200 && fplPercentage < 250) {
    csrEligibility = "Standard (73% Actuarial Value Silver plans)";
  } else {
    csrEligibility = "None (beyond 250% FPL)";
  }

  const postAptcPremium = Math.max(0, (benchmarkPlanCost - aptc) / 12);

  return {
    fplPercentage: fplPercentage,
    aptc: aptc,
    csrEligibility: csrEligibility,
    postAptcPremium: postAptcPremium
  };
}
