const normalize = (value) => Math.max(0, Math.min(10, parseFloat(value) || 0));

const pc = normalize(inputs.policyClarity);
const pe = normalize(inputs.policyEnforcement);
const is = normalize(inputs.incidentSeverity); // Negative factor
const rr = normalize(inputs.retaliationRisk); // Negative factor
const ic = normalize(inputs.intellectualClimate);
const fa = normalize(inputs.facultyAutonomy);
const gi = normalize(inputs.governanceInfluence); // Negative factor
const se = normalize(inputs.studentExpressionSupport);

// Reverse scores for negative factors: higher input value means a worse situation, thus lower contribution to the final score.
const reversedIs = 10 - is; // Severity of incidents: higher is bad, so 10-is makes it good.
const reversedRr = 10 - rr; // Risk of retaliation: higher is bad, so 10-rr makes it good.
const reversedGi = 10 - gi; // External influence: higher is bad, so 10-gi makes it good.

// Calculate weighted score (out of 100). Weights sum to 100 for direct scaling.
let score = (pc * 1.0) +      // Weight 10/100
            (pe * 1.5) +      // Weight 15/100
            (reversedIs * 2.0) + // Weight 20/100 (high impact)
            (reversedRr * 1.5) + // Weight 15/100
            (ic * 1.0) +      // Weight 10/100
            (fa * 1.5) +      // Weight 15/100
            (reversedGi * 0.5) + // Weight 5/100 (lower impact, but still important)
            (se * 1.0);       // Weight 10/100

// Ensure score is within 0-100 range, handling potential floating point inaccuracies near boundaries.
score = Math.max(0, Math.min(100, score));

let riskCategory = "";
if (score >= 90) {
    riskCategory = "Exemplary Commitment to Academic Freedom";
} else if (score >= 75) {
    riskCategory = "Strong, Minor Concerns Identified";
} else if (score >= 50) {
    riskCategory = "Moderate, Significant Areas for Improvement";
} else if (score >= 25) {
    riskCategory = "Weak, High Risk of Infringement";
} else {
    riskCategory = "Severely Lacking, Critical Concerns";
}

return {
    academicFreedomIndex: score,
    riskAssessment: riskCategory
};
