const { age_years, weight_kg, therapy_type, target_estradiol_pmol_l, target_testosterone_nmol_l, target_titration_months, baseline_hormone_level } = inputs;

let initial_dosage_weekly = 0;
let titration_schedule_increment_monthly = 0;

const FEM_STARTING_FACTOR_MG_PER_KG = 0.03; // Rough factor for estradiol
const MASC_STARTING_FACTOR_MG_PER_KG = 0.3; // Rough factor for testosterone
const FEM_BASE_START_DOSE_MG = 1.0; // Minimum starting dose for feminizing
const MASC_BASE_START_DOSE_MG = 20.0; // Minimum starting dose for masculinizing
const FEM_MAX_INITIAL_DOSE_MG = 4.0; // Cap for initial feminizing dose
const MASC_MAX_INITIAL_DOSE_MG = 50.0; // Cap for initial masculinizing dose
const FEM_MAX_TITRATION_INCREMENT_MG_MONTH = 1.0; // Max monthly increment for feminizing
const MASC_MAX_TITRATION_INCREMENT_MG_MONTH = 10.0; // Max monthly increment for masculinizing
const AGE_ADJUSTMENT_FACTOR_MULTIPLIER = 0.05; // Younger patients start lower

let effective_baseline_hormone_level = baseline_hormone_level || 0;

// Age adjustment: younger patients (e.g., 10-14) start with a reduced dose
const age_reduction_factor = Math.max(0, (16 - age_years) * AGE_ADJUSTMENT_FACTOR_MULTIPLIER); // For age 10, factor is (16-10)*0.05 = 0.3 (30% reduction)

if (therapy_type.toLowerCase() === 'feminizing') {
    const target_level = target_estradiol_pmol_l;
    if (!target_level) throw new Error("Target Estradiol Level is required for Feminizing therapy.");

    let base_dose = Math.max(FEM_BASE_START_DOSE_MG, weight_kg * FEM_STARTING_FACTOR_MG_PER_KG);    
    initial_dosage_weekly = base_dose * (1 - age_reduction_factor); // Apply age adjustment
    initial_dosage_weekly = Math.min(initial_dosage_weekly, FEM_MAX_INITIAL_DOSE_MG); // Cap initial dose

    // Adjust initial dose based on baseline, if baseline is significant but below target
    if (effective_baseline_hormone_level > 0 && effective_baseline_hormone_level < target_level) {
        initial_dosage_weekly = Math.max(0.5, initial_dosage_weekly * (1 - Math.pow(effective_baseline_hormone_level / target_level, 0.5) * 0.5));
    }
    initial_dosage_weekly = Math.max(initial_dosage_weekly, 0.5); // Ensure a minimal initial dose

    // Estimate dose needed to reach target level, assuming a linear relationship (simplified)
    // A very rough estimation: 1 mg/week Estradiol might correspond to ~150-200 pmol/L increase
    const estimated_dose_for_target = target_level / 175; // pmol/L per mg/week
    const total_dose_change_needed = Math.max(0, estimated_dose_for_target - initial_dosage_weekly);

    titration_schedule_increment_monthly = Math.max(0.1, total_dose_change_needed / target_titration_months);
    titration_schedule_increment_monthly = Math.min(titration_schedule_increment_monthly, FEM_MAX_TITRATION_INCREMENT_MG_MONTH);

} else if (therapy_type.toLowerCase() === 'masculinizing') {
    const target_level = target_testosterone_nmol_l;
    if (!target_level) throw new Error("Target Testosterone Level is required for Masculinizing therapy.");

    let base_dose = Math.max(MASC_BASE_START_DOSE_MG, weight_kg * MASC_STARTING_FACTOR_MG_PER_KG);    
    initial_dosage_weekly = base_dose * (1 - age_reduction_factor); // Apply age adjustment
    initial_dosage_weekly = Math.min(initial_dosage_weekly, MASC_MAX_INITIAL_DOSE_MG); // Cap initial dose

    // Adjust initial dose based on baseline, if baseline is significant but below target
    if (effective_baseline_hormone_level > 0 && effective_baseline_hormone_level < target_level) {
        initial_dosage_weekly = Math.max(15, initial_dosage_weekly * (1 - Math.pow(effective_baseline_hormone_level / target_level, 0.5) * 0.3));
    }
    initial_dosage_weekly = Math.max(initial_dosage_weekly, 15.0); // Ensure a minimal initial dose

    // Estimate dose needed to reach target level, assuming a linear relationship (simplified)
    // A very rough estimation: 1 mg/week Testosterone might correspond to ~0.7-1.0 nmol/L increase
    const estimated_dose_for_target = target_level / 0.85; // nmol/L per mg/week
    const total_dose_change_needed = Math.max(0, estimated_dose_for_target - initial_dosage_weekly);

    titration_schedule_increment_monthly = Math.max(2.5, total_dose_change_needed / target_titration_months);
    titration_schedule_increment_monthly = Math.min(titration_schedule_increment_monthly, MASC_MAX_TITRATION_INCREMENT_MG_MONTH);

} else {
    throw new Error("Invalid therapy type. Please choose 'Feminizing' or 'Masculinizing'.");
}

// Ensure values are non-negative
initial_dosage_weekly = Math.max(0, initial_dosage_weekly);
titration_schedule_increment_monthly = Math.max(0, titration_schedule_increment_monthly);

return {
    initial_dosage_weekly,
    titration_schedule_increment_monthly,
    estimated_time_to_target_months: target_titration_months
};
