const lc = Math.max(0, landAcquisitionCost);
const n = Math.max(0, numHousingUnits);
const ccu = Math.max(0, constructionCostPerUnit);
const scp = Math.max(0, softCostPercentage);
const dfp = Math.max(0, developerFeePercentage);
const pgs = Math.max(0, publicGrantsSubsidies);
const ec = Math.max(0, equityContribution);
const tar = Math.max(0, targetAffordableRent);
const mrc = Math.max(0, marketRentComparable);
const tas = Math.max(1, Math.min(5, transitAccessibilityScore));

const totalConstructionCost = n * ccu;
const hardCosts = lc + totalConstructionCost;

const softCosts = hardCosts * (scp / 100);
const subTotalDevelopmentCosts = hardCosts + softCosts;
const developerFee = subTotalDevelopmentCosts * (dfp / 100);
const totalDevelopmentCost = subTotalDevelopmentCosts + developerFee;

const totalDevCostSafe = totalDevelopmentCost === 0 ? 1 : totalDevelopmentCost;

const totalSecuredFunding = pgs + ec;
const fundingGapOrSurplus = totalSecuredFunding - totalDevelopmentCost;

const affordabilityGapPerUnit = mrc - tar;

let socialScore = 0;
socialScore += (tas / 5) * 40;
socialScore += Math.min(n / 100, 1) * 30;
socialScore += Math.max(0, Math.min(affordabilityGapPerUnit / 500, 1)) * 30;
const socialViabilityScore = Math.min(100, Math.max(0, socialScore));

let feasibilityScore = 0;

let financialContribution = 0;
if (fundingGapOrSurplus >= 0) {
    const surplusRatio = fundingGapOrSurplus / totalDevCostSafe;
    financialContribution = 50 + Math.min(10, surplusRatio * 200);
} else {
    const gapRatio = Math.abs(fundingGapOrSurplus) / totalDevCostSafe;
    financialContribution = Math.max(0, 50 - gapRatio * 200);
}
feasibilityScore += Math.min(60, Math.max(0, financialContribution));

let affordabilityContribution = 0;
if (affordabilityGapPerUnit > 0) {
    affordabilityContribution = 20 * Math.min(1, affordabilityGapPerUnit / 500);
}
feasibilityScore += Math.min(20, Math.max(0, affordabilityContribution));

feasibilityScore += 20 * (socialViabilityScore / 100);

const finalFeasibilityScore = Math.min(100, Math.max(0, feasibilityScore));

const totalProjectRevenueYear1 = tar * n * 12;

return {
    feasibilityScore: parseFloat(finalFeasibilityScore.toFixed(1)),
    totalDevelopmentCost: parseFloat(totalDevelopmentCost.toFixed(2)),
    fundingGapOrSurplus: parseFloat(fundingGapOrSurplus.toFixed(2)),
    affordabilityGapPerUnit: parseFloat(affordabilityGapPerUnit.toFixed(2)),
    socialViabilityScore: parseFloat(socialViabilityScore.toFixed(1)),
    totalProjectRevenueYear1: parseFloat(totalProjectRevenueYear1.toFixed(2))
};
