function calculateImpacts(inputs) {
    const { 
        policyInvestment, 
        policyHorizonYears, 
        baselineEconomicGrowth, 
        targetedSectorGrowthMultiplier, 
        socialEquityEmphasis, 
        environmentalImpactPriority, 
        aiModelConfidence, 
        publicSentimentIndex 
    } = inputs;

    // Input Validation and Defaults
    const inv = Math.max(0, policyInvestment || 0);
    const hor = Math.max(1, policyHorizonYears || 1); // Horizon must be at least 1 year
    const baseGrowth = (baselineEconomicGrowth || 0) / 100;
    const sectorMult = Math.max(0.5, Math.min(3.0, targetedSectorGrowthMultiplier || 1.0));
    const equity = Math.max(0, Math.min(100, socialEquityEmphasis || 50));
    const confidence = Math.max(0, Math.min(100, aiModelConfidence || 75)) / 100;
    const sentiment = Math.max(0, Math.min(100, publicSentimentIndex || 50));

    let envPriorityFactor = 1.0;
    switch (environmentalImpactPriority.toLowerCase()) {
        case 'high': envPriorityFactor = 1.5; break;
        case 'medium': envPriorityFactor = 1.0; break;
        case 'low': envPriorityFactor = 0.5; break;
        default: envPriorityFactor = 1.0; break; // Default to medium
    }

    // --- Step 1: Calculate Projected Net GDP Impact ---
    // Base economic lift from investment, scaled by horizon and growth multipliers
    const investmentEconomicLift = inv * 0.5 * sectorMult; // Simplified model of investment impact
    const totalGrowthInfluence = (baseGrowth + (sectorMult * 0.01)) * hor; // Combined growth over time

    // Adjusted GDP Impact considering AI Confidence and potential diminishing returns
    const projectedGdpImpact = (investmentEconomicLift * totalGrowthInfluence + inv) * confidence * 0.8; // Factor of 0.8 for real-world complexities

    // Cap GDP impact to prevent unrealistic exponential growth
    const maxGdpImpact = inv * 5 * hor / 10; // Max 0.5x investment per year over horizon
    const finalGdpImpact = Math.min(projectedGdpImpact, maxGdpImpact);

    // --- Step 2: Calculate Overall Social Welfare Score ---
    // Baseline welfare + impact from equity emphasis, public sentiment, and investment magnitude
    const baseWelfare = 50; // Starting point for social welfare
    const equityContribution = equity * 0.3; // Equity emphasis directly boosts welfare
    const sentimentContribution = sentiment * 0.2; // Public sentiment plays a role
    const investmentWelfareBoost = Math.min(20, (inv / 100) * 5); // Limited boost from investment size

    const socialWelfareScore = Math.min(100, baseWelfare + equityContribution + sentimentContribution + investmentWelfareBoost) * confidence * 0.9 + (1-confidence)*50;

    // Ensure a minimum score for welfare
    const finalSocialWelfareScore = Math.max(0, socialWelfareScore);

    // --- Step 3: Calculate Net Environmental Impact Score ---
    // Environmental impact is complex, simplified here: depends on investment (can be positive or negative)
    // and the specific environmental priority set.
    let environmentalImpact = 0;
    const baseEnvImpactFromInvestment = inv * 0.05; // Base impact: investment can have a footprint

    if (environmentalImpactPriority.toLowerCase() === 'high') {
        // High priority means investment likely goes to green initiatives, so positive impact
        environmentalImpact = Math.min(10, baseEnvImpactFromInvestment * 2.5 * (sentiment/100)); // Strong positive
    } else if (environmentalImpactPriority.toLowerCase() === 'medium') {
        // Medium priority means mixed impact, aiming for net neutral or slight positive
        environmentalImpact = Math.min(5, baseEnvImpactFromInvestment * 1.0 * (sentiment/100)); // Moderate positive/neutral
    } else if (environmentalImpactPriority.toLowerCase() === 'low') {
        // Low priority means environmental concerns might be secondary, potentially negative impact
        environmentalImpact = Math.max(-10, -baseEnvImpactFromInvestment * 1.5 * (1-sentiment/100)); // Potentially negative
    }

    const finalEnvironmentalImpactScore = environmentalImpact * confidence + (1-confidence)*0; // AI confidence weights the predicted impact

    // --- Step 4: Calculate Policy Sustainability Index ---
    // Weighted average of key factors, reflecting long-term viability
    const sustainabilityComponents = [
        (finalGdpImpact / (maxGdpImpact > 0 ? maxGdpImpact : 1)) * 30, // Normalize GDP impact to 0-30
        (finalSocialWelfareScore / 100) * 30, // Social welfare 0-30
        (finalEnvironmentalImpactScore + 10) / 20 * 20, // Environmental score normalized to 0-20
        confidence * 10, // AI confidence 0-10
        (sentiment / 100) * 10 // Public sentiment 0-10
    ];

    const policySustainabilityIndex = Math.min(100, sustainabilityComponents.reduce((sum, current) => sum + current, 0));

    return {
        projectedGdpImpact: finalGdpImpact,
        socialWelfareScore: finalSocialWelfareScore,
        environmentalImpactScore: finalEnvironmentalImpactScore,
        policySustainabilityIndex: policySustainabilityIndex
    };
}
