function calculate({ room_length, room_width, room_height, supply_air_flow }) {
    const room_volume = room_length * room_width * room_height;
    let actual_ach = 0;
    let time_to_1_air_change = 0;

    if (room_volume > 0 && supply_air_flow > 0) {
        actual_ach = supply_air_flow / room_volume;
        time_to_1_air_change = (room_volume / supply_air_flow) * 60; // Convert hours to minutes
    } else if (supply_air_flow === 0 && room_volume > 0) {
        actual_ach = 0; // No air flow, no changes
        time_to_1_air_change = Infinity; // Indicates infinite time for an air change
    } else if (room_volume === 0) {
        actual_ach = 0;
        time_to_1_air_change = 0;
    }

    // Ensure ACH is non-negative and cap at a high practical limit if desired (e.g., 600 ACH is extreme)
    actual_ach = Math.max(0, actual_ach);
    time_to_1_air_change = isFinite(time_to_1_air_change) ? Math.max(0, time_to_1_air_change) : time_to_1_air_change;

    return {
        room_volume: room_volume,
        actual_ach: actual_ach,
        time_to_1_air_change: time_to_1_air_change
    };
}
