function calculateRisk({ equipmentAgeYears, lastMajorSoftwareUpdateMonths, avgTrainingHoursPerControllerLastYear, safetyIncidentRateLast5Years, redundancyLevel, cybersecurityAuditFrequencyMonths, preventativeMaintenanceCompliancePercentage, avgControllerExperienceYears }) {

  // Input Validation and Clamping (ensure values are within reasonable bounds for risk calculation)
  equipmentAgeYears = Math.max(0, Math.min(equipmentAgeYears, 40)); // Cap at 40 years
  lastMajorSoftwareUpdateMonths = Math.max(0, Math.min(lastMajorSoftwareUpdateMonths, 72)); // Cap at 6 years
  avgTrainingHoursPerControllerLastYear = Math.max(0, Math.min(avgTrainingHoursPerControllerLastYear, 120)); // Cap at 120 hours/year
  safetyIncidentRateLast5Years = Math.max(0, Math.min(safetyIncidentRateLast5Years, 5)); // Cap at 5 incidents/100k
  cybersecurityAuditFrequencyMonths = Math.max(0, Math.min(cybersecurityAuditFrequencyMonths, 36)); // Cap at 3 years
  preventativeMaintenanceCompliancePercentage = Math.max(0, Math.min(preventativeMaintenanceCompliancePercentage, 100));
  avgControllerExperienceYears = Math.max(0, Math.min(avgControllerExperienceYears, 30)); // Cap at 30 years

  let riskScore = 0;

  // 1. Equipment Age Risk (Weight: 20%)
  // Older equipment -> higher risk. Linear increase, higher acceleration after 15 years.
  let equipmentAgeRisk = 0;
  if (equipmentAgeYears <= 5) equipmentAgeRisk = 0;
  else if (equipmentAgeYears <= 15) equipmentAgeRisk = (equipmentAgeYears - 5) / 10 * 0.4; // 0-0.4 risk for 5-15 years
  else equipmentAgeRisk = 0.4 + ((equipmentAgeYears - 15) / 25 * 0.6); // 0.4-1 risk for 15-40 years
  riskScore += equipmentAgeRisk * 20;

  // 2. Software Update Risk (Weight: 15%)
  // Less frequent updates -> higher risk. Exponential increase.
  let softwareUpdateRisk = 0;
  if (lastMajorSoftwareUpdateMonths <= 6) softwareUpdateRisk = 0; // Updated recently
  else if (lastMajorSoftwareUpdateMonths <= 24) softwareUpdateRisk = (lastMajorSoftwareUpdateMonths - 6) / 18 * 0.4; // 0-0.4 risk for 6-24 months
  else softwareUpdateRisk = 0.4 + ((lastMajorSoftwareUpdateMonths - 24) / 48 * 0.6); // 0.4-1 risk for 24-72 months
  riskScore += softwareUpdateRisk * 15;

  // 3. Personnel Training Risk (Weight: 10%)
  // Lower training hours -> higher risk. Inverse relationship.
  let trainingRisk = 0;
  if (avgTrainingHoursPerControllerLastYear >= 60) trainingRisk = 0;
  else if (avgTrainingHoursPerControllerLastYear >= 30) trainingRisk = (60 - avgTrainingHoursPerControllerLastYear) / 30 * 0.5; // 0-0.5 risk for 30-60 hours
  else trainingRisk = 0.5 + (30 - avgTrainingHoursPerControllerLastYear) / 30 * 0.5; // 0.5-1 risk for 0-30 hours
  riskScore += trainingRisk * 10;

  // 4. Safety Incident Rate Risk (Weight: 25%)
  // Higher incident rate -> very high risk. Direct proportionality.
  let incidentRisk = 0;
  if (safetyIncidentRateLast5Years <= 0.05) incidentRisk = 0;
  else if (safetyIncidentRateLast5Years <= 0.5) incidentRisk = (safetyIncidentRateLast5Years - 0.05) / 0.45 * 0.6; // 0-0.6 risk for 0.05-0.5 incidents
  else incidentRisk = 0.6 + (safetyIncidentRateLast5Years - 0.5) / 4.5 * 0.4; // 0.6-1 risk for 0.5-5 incidents
  riskScore += incidentRisk * 25;

  // 5. Redundancy Level Risk (Weight: 10%)
  // Lower redundancy -> higher risk. Categorical mapping.
  let redundancyRisk = 0;
  switch (redundancyLevel) {
    case "Minimal (Single Points of Failure)":
      redundancyRisk = 1;
      break;
    case "Partial (Some Critical Systems Redundant)":
      redundancyRisk = 0.5;
      break;
    case "High (Full Redundancy & Independent Backups)":
      redundancyRisk = 0;
      break;
  }
  riskScore += redundancyRisk * 10;

  // 6. Cybersecurity Audit Frequency Risk (Weight: 8%)
  // Less frequent audits -> higher risk. Inverted, linear until critical.
  let cybersecurityRisk = 0;
  if (cybersecurityAuditFrequencyMonths <= 6) cybersecurityRisk = 0;
  else if (cybersecurityAuditFrequencyMonths <= 18) cybersecurityRisk = (cybersecurityAuditFrequencyMonths - 6) / 12 * 0.4; // 0-0.4 for 6-18 months
  else cybersecurityRisk = 0.4 + (cybersecurityAuditFrequencyMonths - 18) / 18 * 0.6; // 0.4-1 for 18-36 months
  riskScore += cybersecurityRisk * 8;

  // 7. Preventative Maintenance Compliance Risk (Weight: 7%)
  // Lower compliance -> higher risk. Inverse proportionality.
  let maintenanceRisk = 0;
  if (preventativeMaintenanceCompliancePercentage >= 98) maintenanceRisk = 0;
  else if (preventativeMaintenanceCompliancePercentage >= 80) maintenanceRisk = (98 - preventativeMaintenanceCompliancePercentage) / 18 * 0.6; // 0-0.6 for 80-98%
  else maintenanceRisk = 0.6 + (80 - preventativeMaintenanceCompliancePercentage) / 80 * 0.4; // 0.6-1 for 0-80%
  riskScore += maintenanceRisk * 7;

  // 8. Controller Experience Risk (Weight: 5%)
  // Lower average experience -> higher risk. Inverse proportionality.
  let experienceRisk = 0;
  if (avgControllerExperienceYears >= 15) experienceRisk = 0;
  else if (avgControllerExperienceYears >= 5) experienceRisk = (15 - avgControllerExperienceYears) / 10 * 0.7; // 0-0.7 for 5-15 years
  else experienceRisk = 0.7 + (5 - avgControllerExperienceYears) / 5 * 0.3; // 0.7-1 for 0-5 years
  riskScore += experienceRisk * 5;

  // Normalize total risk score to 0-100
  const overallRiskScore = Math.max(0, Math.min(riskScore, 100));

  let riskCategory = "Low";
  let recommendationSeverity = "Routine Monitoring";

  if (overallRiskScore >= 75) {
    riskCategory = "Critical";
    recommendationSeverity = "Immediate Action Required";
  } else if (overallRiskScore >= 55) {
    riskCategory = "High";
    recommendationSeverity = "Urgent Review & Remediation";
  } else if (overallRiskScore >= 35) {
    riskCategory = "Moderate";
    recommendationSeverity = "Proactive Planning & Improvement";
  } else if (overallRiskScore >= 15) {
    riskCategory = "Low-Moderate";
    recommendationSeverity = "Regular Assessment & Optimization";
  }

  return {
    overallRiskScore: parseFloat(overallRiskScore.toFixed(1)),
    riskCategory,
    recommendationSeverity
  };
}
