const initial = inputs['initial-investment'];
const final = inputs['final-value'];
const years = inputs['investment-period-years'];

const errors = [];
if (initial <= 0) {
    errors.push('Initial investment must be a positive number.');
}
if (years <= 0) {
    errors.push('Investment period must be a positive number of years.');
}
if (final < 0) {
    errors.push('Final investment value cannot be negative.');
}

if (errors.length > 0) {
    return {
        'annualized-return': NaN,
        'total-return': NaN,
        'total-profit-loss': NaN,
        _errors: errors
    };
}

const totalProfitLoss = final - initial;
const totalReturnDecimal = totalProfitLoss / initial;
const totalReturnPercent = totalReturnDecimal * 100;

let annualizedReturnDecimal;
if (initial === final) {
    annualizedReturnDecimal = 0; // If initial and final are same, annual return is 0.
} else {
    annualizedReturnDecimal = Math.pow(final / initial, 1 / years) - 1;
}

const annualizedReturnPercent = annualizedReturnDecimal * 100;

// Cap annualized return to prevent extremely large or small numbers due to edge cases
const MAX_DISPLAY_RETURN = 10000; // 10,000%
const MIN_DISPLAY_RETURN = -99.99; // Cannot lose more than 100% of initial investment if initial > 0

const cappedAnnualizedReturn = Math.min(Math.max(annualizedReturnPercent, MIN_DISPLAY_RETURN), MAX_DISPLAY_RETURN);
const cappedTotalReturn = Math.min(Math.max(totalReturnPercent, MIN_DISPLAY_RETURN), MAX_DISPLAY_RETURN);

return {
    'annualized-return': cappedAnnualizedReturn,
    'total-return': cappedTotalReturn,
    'total-profit-loss': totalProfitLoss
};
