const initial = parseFloat(inputs.initialInvestment) || 0;
const final = parseFloat(inputs.finalValue) || 0;
const years = parseFloat(inputs.years) || 0;

if (initial === 0 || years === 0) return { cagr: 0, totalReturn: 0, profit: 0 };

const totalProfit = final - initial;
const totalReturn = (totalProfit / initial) * 100;

// CAGR Formula: (Ending / Beginning)^(1/n) - 1
let cagr = 0;
if (final > 0 && initial > 0) {
    cagr = (Math.pow(final / initial, 1 / years) - 1) * 100;
}

return {
    cagr: cagr,
    totalReturn: totalReturn,
    profit: totalProfit
};
