const P = inputs.payment_amount;
const r_annual = inputs.annual_interest_rate / 100;
const t = inputs.number_of_years;
const m = inputs.payments_per_year;

// Assuming compounding frequency equals payment frequency for simplicity
const i = r_annual / m; // Periodic interest rate
const n = t * m;        // Total number of periods

let fv_ordinary_annuity;
let pv_ordinary_annuity;
let fv_annuity_due;
let pv_annuity_due;

if (i === 0) {
    // Handle zero interest rate case (sum of payments)
    fv_ordinary_annuity = P * n;
    pv_ordinary_annuity = P * n;
    fv_annuity_due = P * n;
    pv_annuity_due = P * n;
} else {
    // Ordinary Annuity Formulas
    const term_fv_ordinary = (Math.pow(1 + i, n) - 1) / i;
    fv_ordinary_annuity = P * term_fv_ordinary;

    const term_pv_ordinary = (1 - Math.pow(1 + i, -n)) / i;
    pv_ordinary_annuity = P * term_pv_ordinary;

    // Annuity Due Formulas (Ordinary Annuity * (1 + i))
    fv_annuity_due = fv_ordinary_annuity * (1 + i);
    pv_annuity_due = pv_ordinary_annuity * (1 + i);
}

return {
    fv_ordinary_annuity: fv_ordinary_annuity,
    pv_ordinary_annuity: pv_ordinary_annuity,
    fv_annuity_due: fv_annuity_due,
    pv_annuity_due: pv_annuity_due
};
