function calculate({ tankVolume, currentAlk, targetAlk, alkProductConcentration_dKH_per_mL_per_liter, currentCa, targetCa, caProductConcentration_ppm_per_mL_per_liter }) {
    let requiredAlkDose = 0;
    let requiredCaDose = 0;

    if (tankVolume <= 0) {
        return { requiredAlkDose: 0, requiredCaDose: 0, error: "Tank volume must be positive." };
    }

    // Calculate Alkalinity Dose
    const alkDifference = targetAlk - currentAlk;
    if (alkDifference > 0) {
        if (alkProductConcentration_dKH_per_mL_per_liter <= 0) {
            requiredAlkDose = 0; // Cannot dose if product concentration is zero or negative
        } else {
            requiredAlkDose = (alkDifference / alkProductConcentration_dKH_per_mL_per_liter) * tankVolume;
        }
    }

    // Calculate Calcium Dose
    const caDifference = targetCa - currentCa;
    if (caDifference > 0) {
        if (caProductConcentration_ppm_per_mL_per_liter <= 0) {
            requiredCaDose = 0; // Cannot dose if product concentration is zero or negative
        } else {
            requiredCaDose = (caDifference / caProductConcentration_ppm_per_mL_per_liter) * tankVolume;
        }
    }

    return {
        requiredAlkDose: Math.max(0, requiredAlkDose),
        requiredCaDose: Math.max(0, requiredCaDose)
    };
}
