const IE_THRESHOLD_J_CM2 = 5; // Approx. 1.2 cal/cm^2 for 2nd degree burn
const AFB_SCALING_EXPONENT = 1.8; // Common exponent for arc flash energy decay with distance
const MIN_ARC_DURATION_CYCLES = 2; // Minimum arc duration for calculation stability
const REFERENCE_VOLTAGE_KV = 13.8; // A common medium voltage reference
const REFERENCE_FAULT_CURRENT_KA = 10; // A common fault current reference
const REFERENCE_TIME_CYCLES = 9; // Approx 0.15s at 60Hz

// Input values
let incidentEnergyAtRef = inputs.incident_energy_at_reference_distance; // J/cm^2
let referenceDistance = inputs.reference_distance_meters; // meters
let arcDuration = Math.max(inputs.arc_duration_cycles, MIN_ARC_DURATION_CYCLES); // cycles
let systemVoltage = inputs.system_voltage_kv; // kV
let faultCurrent = inputs.available_fault_current_ka; // kA

// --- Calculation for Arc Flash Boundary ---
// Adjust reference incident energy based on the other system parameters for a quick estimate.
// These factors are simplified and illustrative, providing a 'directional' adjustment.
// They mimic general trends where higher voltage, current, and duration increase energy.
let voltageFactor = Math.pow(systemVoltage / REFERENCE_VOLTAGE_KV, 0.4); // Voltage influence
let faultCurrentFactor = Math.pow(faultCurrent / REFERENCE_FAULT_CURRENT_KA, 0.3); // Fault current influence
let timeFactor = arcDuration / REFERENCE_TIME_CYCLES; // Arc duration influence

let adjustedIncidentEnergyForBoundary = incidentEnergyAtRef * voltageFactor * faultCurrentFactor * timeFactor;

let arcFlashBoundary = 0;
if (adjustedIncidentEnergyForBoundary > IE_THRESHOLD_J_CM2) {
    // Calculate boundary using inverse power law from the adjusted reference energy
    arcFlashBoundary = referenceDistance * Math.pow(adjustedIncidentEnergyForBoundary / IE_THRESHOLD_J_CM2, 1 / AFB_SCALING_EXPONENT);
    // Cap the boundary to a reasonable maximum for a "quick estimate" tool, indicating limitations.
    arcFlashBoundary = Math.min(arcFlashBoundary, 25); // Cap at 25 meters
} else {
    // If adjusted energy is already below the threshold, the boundary is effectively 0.
    arcFlashBoundary = 0;
}

// --- Calculation for Incident Energy at 1.2 meters (approx 4 feet) ---
// This uses the basic inverse power law, scaling from the *original* input reference incident energy.
// It helps provide context for the arc flash intensity at a common working distance.
let incidentEnergyAt1_2m;
if (referenceDistance === 1.2) {
    incidentEnergyAt1_2m = incidentEnergyAtRef;
} else {
    incidentEnergyAt1_2m = incidentEnergyAtRef * Math.pow(referenceDistance / 1.2, AFB_SCALING_EXPONENT);
}
// Cap the value for display if it becomes excessively high.
incidentEnergyAt1_2m = Math.min(incidentEnergyAt1_2m, 1000); // Max reasonable IE for display

return {
    arc_flash_boundary_meters: parseFloat(arcFlashBoundary.toFixed(2)),
    incident_energy_at_1_2_meters: parseFloat(incidentEnergyAt1_2m.toFixed(2))
};
