/**
 * Calculates the environmental, socio-economic, cultural, and climate vulnerability scores
 * for an Arctic resource development project.
 * All scores are normalized to a 0-100 range, where higher scores indicate higher risk/impact.
 *
 * Inputs:
 * - projectFootprint (sq km): Physical area covered by the project.
 * - ecosystemSensitivity (1-5): Sensitivity of local ecosystems.
 * - carbonEmissionIntensity (tonnes CO2e/year): Annual direct GHG emissions.
 * - waterUsageIntensity (m³/day): Daily freshwater usage.
 * - wasteGenerationVolume (tonnes/year): Annual hazardous waste generated.
 * - indigenousProximity (km): Distance to the closest indigenous community.
 * - culturalHeritageImpact (1-5): Potential for direct cultural heritage impact.
 * - permafrostThawVulnerability (1-5): Local permafrost stability under warming.
 * - seaIceDependency (1-5): Project's operational reliance on stable sea ice conditions.
 *
 * Outputs:
 * - environmentalRiskScore (0-100)
 * - socioEconomicRiskScore (0-100)
 * - culturalRiskScore (0-100)
 * - climateVulnerabilityScore (0-100)
 * - overallImpactScore (0-100)
 */

function calculateScores(inputs) {
    const { projectFootprint, ecosystemSensitivity, carbonEmissionIntensity, waterUsageIntensity, 
            wasteGenerationVolume, indigenousProximity, culturalHeritageImpact, 
            permafrostThawVulnerability, seaIceDependency } = inputs;

    // --- Input Normalization and Scaling Constants ---
    // These constants are heuristic and designed to bring diverse inputs into a comparable scale.
    // They represent a typical maximum 'high risk' value for each input.
    const MAX_FOOTPRINT = 500; // sq km (large project) - projects above this still scale, but impact plateaus
    const MAX_CARBON_EMISSIONS = 500000; // tonnes CO2e/year (very high emitter)
    const MAX_WATER_USAGE = 10000; // m³/day (very high water consumer)
    const MAX_WASTE_GENERATION = 2000; // tonnes/year (significant waste)
    const MIN_INDIGENOUS_PROXIMITY_RISK_THRESHOLD = 5; // km (below this, risk escalates rapidly)
    const MAX_INDIGENOUS_PROXIMITY_RISK_ZERO = 100; // km (beyond this, direct proximity risk is low)

    // Clamp inputs to reasonable non-negative values for calculation stability
    const clampedFootprint = Math.max(0, projectFootprint);
    const clampedCarbonEmissions = Math.max(0, carbonEmissionIntensity);
    const clampedWaterUsage = Math.max(0, waterUsageIntensity);
    const clampedWasteGeneration = Math.max(0, wasteGenerationVolume);
    const clampedIndigenousProximity = Math.max(0.1, indigenousProximity); // Ensure no division by zero

    // --- Environmental Risk Score Components (Max 100) ---
    let envScore = 0;
    // Project footprint (scaled, higher values increase risk significantly)
    envScore += Math.min(50, (clampedFootprint / MAX_FOOTPRINT) * 50);
    // Ecosystem Sensitivity (direct scaling, high sensitivity is high risk)
    envScore += (ecosystemSensitivity / 5) * 20;
    // Water Usage (scaled, higher usage increases risk)
    envScore += Math.min(15, (clampedWaterUsage / MAX_WATER_USAGE) * 15);
    // Waste Generation (scaled, higher waste increases risk)
    envScore += Math.min(15, (clampedWasteGeneration / MAX_WASTE_GENERATION) * 15);

    const environmentalRiskScore = Math.min(100, Math.max(0, envScore));

    // --- Socio-Economic Impact Score Components (Max 100) ---
    let socioEcoScore = 0;
    // Indigenous proximity (inverse relationship: closer = higher risk)
    // Use an inverse squared relationship for rapid escalation when very close.
    const proximityFactor = Math.pow(Math.max(1, clampedIndigenousProximity / MIN_INDIGENOUS_PROXIMITY_RISK_THRESHOLD), -2);
    socioEcoScore += Math.min(70, proximityFactor * 70); // High impact for very close projects

    // Assume general project size also contributes to socio-economic change risk
    socioEcoScore += Math.min(30, (clampedFootprint / MAX_FOOTPRINT) * 30); // Larger projects, more socio-eco change

    const socioEconomicRiskScore = Math.min(100, Math.max(0, socioEcoScore));

    // --- Cultural Heritage Risk Score Components (Max 100) ---
    let culturalScore = 0;
    // Direct cultural heritage impact potential (direct scaling)
    culturalScore += (culturalHeritageImpact / 5) * 60;
    // Indirect cultural impact from proximity (weighted, higher risk for closer projects)
    const culturalProximityFactor = Math.min(1, Math.max(0, 1 - (clampedIndigenousProximity / MAX_INDIGENOUS_PROXIMITY_RISK_ZERO)));
    culturalScore += culturalProximityFactor * 40; 

    const culturalRiskScore = Math.min(100, Math.max(0, culturalScore));

    // --- Climate Vulnerability Score Components (Max 100) ---
    let climateScore = 0;
    // Direct carbon emissions (scaled)
    climateScore += Math.min(40, (clampedCarbonEmissions / MAX_CARBON_EMISSIONS) * 40);
    // Permafrost thaw vulnerability (direct scaling)
    climateScore += (permafrostThawVulnerability / 5) * 30;
    // Sea ice dependency (direct scaling, higher dependency means higher operational vulnerability to climate change)
    climateScore += (seaIceDependency / 5) * 30;

    const climateVulnerabilityScore = Math.min(100, Math.max(0, climateScore));

    // --- Overall Impact Score (Weighted Average) ---
    // Weights reflect the relative importance assigned to each category.
    const overallImpactScore = Math.round(
        (environmentalRiskScore * 0.35) + 
        (socioEconomicRiskScore * 0.20) + 
        (culturalRiskScore * 0.25) + 
        (climateVulnerabilityScore * 0.20)
    );

    return {
        environmentalRiskScore: Math.round(environmentalRiskScore),
        socioEconomicRiskScore: Math.round(socioEconomicRiskScore),
        culturalRiskScore: Math.round(culturalRiskScore),
        climateVulnerabilityScore: Math.round(climateVulnerabilityScore),
        overallImpactScore: Math.round(Math.min(100, Math.max(0, overallImpactScore)))
    };
}
