/**
 * Calculates the CO2 emissions and fuel consumption for Arctic and Traditional shipping routes.
 *
 * @param {object} inputs - The input values from the calculator form.
 * @param {number} inputs.vesselCapacityTEU - The capacity of the vessel in Twenty-foot Equivalent Units (TEU).
 * @param {number} inputs.vesselSpeedKnots - The average transit speed of the vessel in knots.
 * @param {string} inputs.fuelType - The primary fuel type used by the vessel (e.g., 'HFO', 'MGO', 'LNG').
 * @param {number} inputs.arcticRouteDistanceNM - The total distance of the Arctic route in Nautical Miles.
 * @param {number} inputs.traditionalRouteDistanceNM - The total distance of the traditional route in Nautical Miles.
 * @param {string} inputs.iceClassRating - The ice class rating of the vessel (e.g., 'PC6', 'None').
 * @param {number} inputs.operationalDaysInIce - The number of operational days spent in ice-affected waters on the Arctic route.
 * @param {number} inputs.vesselEfficiencyFactor - A factor representing the specific efficiency of the vessel (e.g., 0.8 for highly efficient, 1.2 for less efficient).
 *
 * @returns {object} An object containing the calculated fuel consumption and CO2 emissions for both routes, and their differences.
 */
function calculateCarbonFootprint(inputs) {
  const {
    vesselCapacityTEU,
    vesselSpeedKnots,
    fuelType,
    arcticRouteDistanceNM,
    traditionalRouteDistanceNM,
    iceClassRating,
    operationalDaysInIce,
    vesselEfficiencyFactor
  } = inputs;

  // --- Constants and Validation ---

  // Base fuel consumption rate (tons fuel / TEU / hour at REFERENCE_SPEED)
  // This value is derived from typical container ship consumption (e.g., 120-150 tons/day for 8000 TEU at 15 knots)
  // 120 tons/day for 8000 TEU at 15 knots: (120/24) tons/hour / 8000 TEU = 0.000625 tons/TEU/hour
  const FUEL_CONSUMPTION_PER_TEU_HOUR_AT_REFERENCE_SPEED = 0.000625;
  const REFERENCE_SPEED = 15; // knots

  // CO2 Emission Factors (tCO2 / ton_fuel)
  const emissionFactors = {
    'HFO': 3.114,
    'MGO': 3.151,
    'LNG': 2.75, // Lower CO2 per unit energy, but actual lifecycle can vary (methane slip not included for direct CO2 combustion)
    'Methanol': 1.375,
    'Ammonia': 0 // Zero direct CO2 emissions at combustion point
  };

  // Ice Class Multipliers (factor to increase fuel consumption when operating in ice)
  const iceClassMultipliers = {
    'None': 1.05, // Slight penalty for non-ice-class vessel in ice-affected waters (even if not thick ice)
    'PC7': 1.15,
    'PC6': 1.25,
    'PC5': 1.4,
    'PC4': 1.6,
    'PC3': 1.8,
    'PC2': 2.0,
    'PC1': 2.2
  };

  // Validate inputs and apply caps
  const validatedVesselCapacityTEU = Math.max(1, vesselCapacityTEU);
  const validatedVesselSpeedKnots = Math.max(0.1, vesselSpeedKnots); // Minimum speed to avoid division by zero
  const validatedArcticRouteDistanceNM = Math.max(0, arcticRouteDistanceNM);
  const validatedTraditionalRouteDistanceNM = Math.max(0, traditionalRouteDistanceNM);
  const validatedOperationalDaysInIce = Math.max(0, operationalDaysInIce);
  // Cap efficiency factor to prevent extreme values, allowing for a range of -20% to +100% inefficiency
  const validatedVesselEfficiencyFactor = Math.max(0.8, Math.min(2.0, vesselEfficiencyFactor)); 

  // --- Calculation Steps ---

  // 1. Calculate the base fuel consumption rate per TEU-hour at the specified vessel speed.
  // Fuel consumption is assumed to be proportional to (Speed / Reference Speed)^2 for simplified modeling.
  const fuelConsumptionPerTEU_Hour = FUEL_CONSUMPTION_PER_TEU_HOUR_AT_REFERENCE_SPEED *
    Math.pow(validatedVesselSpeedKnots / REFERENCE_SPEED, 2);

  // 2. Determine the ice factor based on the vessel's ice class rating.
  const iceFactor = iceClassMultipliers[iceClassRating] || iceClassMultipliers['None'];

  // 3. Calculate fuel consumption for the Arctic Route.
  let totalArcticFuel = 0;
  if (validatedArcticRouteDistanceNM > 0) {
    const totalArcticHours = validatedArcticRouteDistanceNM / validatedVesselSpeedKnots;
    const theoreticalHoursInIce = validatedOperationalDaysInIce * 24; // Convert days to hours
    // Actual hours in ice cannot exceed total Arctic hours
    const actualHoursInIce = Math.min(totalArcticHours, theoreticalHoursInIce);
    const hoursOpenWaterArctic = totalArcticHours - actualHoursInIce;

    const fuelArcticOpenWater = validatedVesselCapacityTEU * fuelConsumptionPerTEU_Hour * hoursOpenWaterArctic;
    const fuelArcticInIce = validatedVesselCapacityTEU * fuelConsumptionPerTEU_Hour * actualHoursInIce * iceFactor;
    totalArcticFuel = (fuelArcticOpenWater + fuelArcticInIce) * validatedVesselEfficiencyFactor;
  }

  // 4. Calculate fuel consumption for the Traditional Route.
  let totalTraditionalFuel = 0;
  if (validatedTraditionalRouteDistanceNM > 0) {
    const totalTraditionalHours = validatedTraditionalRouteDistanceNM / validatedVesselSpeedKnots;
    totalTraditionalFuel = validatedVesselCapacityTEU * fuelConsumptionPerTEU_Hour * totalTraditionalHours * validatedVesselEfficiencyFactor;
  }

  // 5. Calculate CO2 emissions for both routes.
  const co2Factor = emissionFactors[fuelType] || emissionFactors['HFO']; // Default to HFO if fuel type is not recognized
  const arcticCO2Emissions = totalArcticFuel * co2Factor;
  const traditionalCO2Emissions = totalTraditionalFuel * co2Factor;

  // 6. Calculate the difference and percentage change in CO2 emissions.
  const co2Difference = traditionalCO2Emissions - arcticCO2Emissions; // Positive means Arctic route saves CO2
  let percentageDifference = 0;
  if (traditionalCO2Emissions > 0) {
    percentageDifference = (co2Difference / traditionalCO2Emissions) * 100;
  }

  return {
    arcticFuelConsumption: totalArcticFuel,
    arcticCO2Emissions: arcticCO2Emissions,
    traditionalFuelConsumption: totalTraditionalFuel,
    traditionalCO2Emissions: traditionalCO2Emissions,
    co2Difference: co2Difference,
    percentageDifference: percentageDifference
  };
}
