const bodyWeightKg = inputs.body_weight_kg;
const activityDurationHours = inputs.activity_duration_hours;
const intensityLevel = inputs.activity_intensity_level; // 1-10
const ambientTempC = inputs.ambient_temperature_c;
const relativeHumidityPercent = inputs.relative_humidity_percent;
const sweatSodiumMgL = inputs.individual_sweat_sodium_mg_l;

// Input validation and default fallback if any input is zero or invalid, though required should prevent this.
// For calculation safety, ensure positive values.
if (bodyWeightKg <= 0 || activityDurationHours <= 0 || intensityLevel <= 0 || sweatSodiumMgL < 0) {
    return { total_fluid_replacement_liters: 0, total_sodium_replacement_mg: 0 };
}

// Base sweat rate estimation factors
// Average base sweat rate at very low intensity for a 70kg person is roughly 0.5 L/hr (500 ml/hr).
// Using ml/kg/hr for a more robust base, e.g., 7 ml/kg/hr at very low intensity.
const baseSweatRatePerKgMlHr = 7; 

// Intensity multiplier: 1 (low) to 3.5 (very high)
// A level 1 (min) maps to a multiplier of 1, level 10 (max) maps to 3.5
const intensityMultiplier = 1 + ((intensityLevel - 1) / 9) * 2.5; 

// Temperature factor: Increases sweat rate significantly above 20°C.
// For every degree above 20°C, sweat rate increases by ~5%.
const tempFactor = 1 + Math.max(0, (ambientTempC - 20) * 0.05);

// Humidity factor: High humidity impairs evaporative cooling, increasing perceived heat stress and often sweat rate.
// Increases sweat rate by up to 20% at 100% humidity.
const humidityFactor = 1 + (relativeHumidityPercent / 100) * 0.20;

// Estimated overall sweat rate in ml/hour
let estimatedSweatRateMlHr = bodyWeightKg * baseSweatRatePerKgMlHr * intensityMultiplier * tempFactor * humidityFactor;

// Cap estimated sweat rate to a realistic maximum to prevent extreme, unrealistic values.
// For a 70kg person, max could be around 4 L/hr in extreme conditions.
const maxSweatRateMlHrPerKg = 50; // Max ml/kg/hr
estimatedSweatRateMlHr = Math.min(estimatedSweatRateMlHr, bodyWeightKg * maxSweatRateMlHrPerKg);
// Absolute general maximum cap regardless of weight (e.g., 4.5 L/hr)
estimatedSweatRateMlHr = Math.min(estimatedSweatRateMlHr, 4500);

// Total fluid lost during activity (ml)
const totalFluidLostMl = estimatedSweatRateMlHr * activityDurationHours;

// Recommended fluid replacement: Typically 80-100% of total fluid lost. Targeting 90% for active replacement.
let totalFluidReplacementLiters = (totalFluidLostMl * 0.9) / 1000;

// Apply a minimum hourly fluid intake for any activity to ensure basic hydration, e.g., 0.25L/hr.
if (activityDurationHours > 0) {
    totalFluidReplacementLiters = Math.max(totalFluidReplacementLiters, activityDurationHours * 0.25);
}

// Cap hourly fluid intake to avoid overhydration (e.g., max 1.5L/hr) during activity.
// This ensures a practical limit on intake recommendations.
totalFluidReplacementLiters = Math.min(totalFluidReplacementLiters, activityDurationHours * 1.5);

// Sodium replacement based on fluid replacement and individual sweat sodium concentration.
let totalSodiumReplacementMg = totalFluidReplacementLiters * sweatSodiumMgL;

// Ensure sodium output is non-negative.
totalSodiumReplacementMg = Math.max(0, totalSodiumReplacementMg);

return {
    total_fluid_replacement_liters: totalFluidReplacementLiters,
    total_sodium_replacement_mg: totalSodiumReplacementMg
};
