formula:
const weightBefore = parseFloat(inputs.weightBefore) || 0;
const weightAfter = parseFloat(inputs.weightAfter) || 0;
const minutes = parseFloat(inputs.duration) || 60;
const consumedOz = parseFloat(inputs.fluidConsumed) || 0;

// 1 lb = 16 oz of water
const weightLossLbs = weightBefore - weightAfter;
const weightLossOz = weightLossLbs * 16;

const totalSweatLossOz = weightLossOz + consumedOz;
const hourlySweatRate = (totalSweatLossOz / minutes) * 60;

// Sodium estimates (mg per liter of sweat)
// Light: 500mg/L, Avg: 900mg/L, Heavy: 1500mg/L
// 1 Liter approx 33.8 oz
let sodiumPerOz = 0;
if (inputs.sweaterType.includes('Light')) sodiumPerOz = 500 / 33.8;
else if (inputs.sweaterType.includes('Average')) sodiumPerOz = 900 / 33.8;
else sodiumPerOz = 1500 / 33.8;

const totalSodiumLoss = totalSweatLossOz * sodiumPerOz;

return {
    sweatRate: hourlySweatRate,
    fluidDeficit: weightLossOz,
    sodiumNeeds: totalSodiumLoss
};
