function calculateHydration(inputs) {
  // Input Validation and Defaults
  let bodyWeightKg = Math.max(1, inputs.bodyWeightKg || 0);
  let activityDurationHours = Math.max(0.1, inputs.activityDurationHours || 0.1); // Min 0.1 to avoid division by zero
  let activityIntensity = Math.min(10, Math.max(1, inputs.activityIntensity || 5));
  let ambientTemperatureC = inputs.ambientTemperatureC || 20;
  let relativeHumidityPercent = Math.min(100, Math.max(0, inputs.relativeHumidityPercent || 50));
  let individualSweatRateMLHr = inputs.individualSweatRateMLHr;
  let acclimatizationFactor = Math.min(1, Math.max(0, inputs.acclimatizationFactor || 0));
  let clothingCoverage = Math.min(1, Math.max(0, inputs.clothingCoverage || 0));

  // Constants
  const DEFAULT_SWEAT_RATE_FACTOR_ML_PER_KG_PER_HOUR = 10; // Baseline for moderate activity
  const INTENSITY_MULTIPLIER_BASE = 0.7; // For intensity 1
  const INTENSITY_MULTIPLIER_SCALE = 1.3; // Adds up to 1.3 * (10/10) for intensity 10
  const TEMP_MULTIPLIER_PER_DEGREE_ABOVE_20C = 0.05; // 5% increase in sweat for every degree above 20C
  const HUMIDITY_PENALTY_PER_PERCENT_ABOVE_50 = 0.005; // 0.5% increase for every % humidity above 50%
  const ACCLIMATIZATION_REDUCTION_MAX = 0.15; // Max 15% reduction in heat stress impact on sweat
  const CLOTHING_PENALTY_MAX = 0.20; // Max 20% increase in sweat due to full clothing
  const SODIUM_PER_LITER_SWEAT_MG = 900; // Average sodium concentration in sweat
  const REPLACEMENT_PERCENTAGE = 0.95; // Aim to replace 95% of estimated sweat loss
  const MAX_HOURLY_INTAKE_ML = 1500; // Cap at 1.5L/hr to prevent hyponatremia
  const BASE_HYDRATION_BOLUS_ML = 100; // Small initial bolus per hour

  // 1. Calculate Base Sweat Rate
  let baseSweatRateMLHr;
  if (individualSweatRateMLHr > 50) { // Use known rate if provided and reasonable
    baseSweatRateMLHr = individualSweatRateMLHr;
  } else {
    baseSweatRateMLHr = bodyWeightKg * DEFAULT_SWEAT_RATE_FACTOR_ML_PER_KG_PER_HOUR;
  }

  // Adjust for activity intensity
  const intensityMultiplier = INTENSITY_MULTIPLIER_BASE + (activityIntensity / 10 * INTENSITY_MULTIPLIER_SCALE);
  baseSweatRateMLHr *= intensityMultiplier;

  // 2. Environmental Adjustment
  let environmentalMultiplier = 1;

  // Temperature effect
  if (ambientTemperatureC > 20) {
    environmentalMultiplier += (ambientTemperatureC - 20) * TEMP_MULTIPLIER_PER_DEGREE_ABOVE_20C;
  }

  // Humidity effect
  if (relativeHumidityPercent > 50) {
    environmentalMultiplier += (relativeHumidityPercent - 50) * HUMIDITY_PENALTY_PER_PERCENT_ABOVE_50;
  }

  // Acclimatization effect (reduces environmental impact)
  environmentalMultiplier -= acclimatizationFactor * ACCLIMATIZATION_REDUCTION_MAX; // Max reduction of 15%
  environmentalMultiplier = Math.max(0.8, environmentalMultiplier); // Ensure multiplier doesn't go too low

  // Clothing coverage effect (increases environmental impact)
  environmentalMultiplier += clothingCoverage * CLOTHING_PENALTY_MAX;

  // 3. Total Estimated Sweat Loss
  let adjustedSweatRateMLHr = baseSweatRateMLHr * environmentalMultiplier;
  // Clamp adjusted sweat rate to realistic physiological bounds
  adjustedSweatRateMLHr = Math.max(200, Math.min(2500, adjustedSweatRateMLHr)); // Min 200ml/hr, Max 2500ml/hr

  let totalSweatLossML = adjustedSweatRateMLHr * activityDurationHours;

  // 4. Calculate Fluid Intake Recommendations
  let totalFluidIntakeML = totalSweatLossML * REPLACEMENT_PERCENTAGE;

  // Add a small base hydration bolus, especially for shorter activities
  totalFluidIntakeML += activityDurationHours * BASE_HYDRATION_BOLUS_ML;
  totalFluidIntakeML = Math.max(totalFluidIntakeML, 250 * activityDurationHours); // Ensure minimum intake

  let hourlyFluidIntakeML = totalFluidIntakeML / activityDurationHours;

  // Cap hourly intake to prevent over-hydration/hyponatremia risk
  hourlyFluidIntakeML = Math.min(hourlyFluidIntakeML, MAX_HOURLY_INTAKE_ML);
  // Recalculate total intake based on capped hourly rate for consistency
  totalFluidIntakeML = hourlyFluidIntakeML * activityDurationHours;

  // 5. Calculate Sodium Replacement
  const totalSweatLiters = totalSweatLossML / 1000;
  let sodiumReplacementMG = totalSweatLiters * SODIUM_PER_LITER_SWEAT_MG;

  // 6. Electrolyte Solution Recommendation
  let electrolyteRecommendation = "Water, potentially with light electrolytes (e.g., 200-300mg Sodium/L)";
  if (activityDurationHours > 2 && totalFluidIntakeML > 2000) {
    electrolyteRecommendation = "Isotonic Sports Drink (6-8% Carbohydrate, 400-800mg Sodium/L, 20-30mEq Potassium/L)";
  } else if (activityDurationHours > 1.5 && totalFluidIntakeML > 1200) {
    electrolyteRecommendation = "Hypotonic Sports Drink or Water with Electrolyte Tablets (300-500mg Sodium/L, 10-20mEq Potassium/L)";
  }

  return {
    totalFluidIntakeML: Math.round(totalFluidIntakeML),
    hourlyFluidIntakeML: Math.round(hourlyFluidIntakeML),
    sodiumReplacementMG: Math.round(sodiumReplacementMG),
    electrolyteRecommendation: electrolyteRecommendation
  };
}
