const { currentPerformanceValue, performanceMetricType, trainingSessionsPerWeek, averageRPE, recoveryScore, trainingCycleWeeks, experienceLevel } = inputs;

let baseGainFactor = 0.005; // Base percentage gain factor per week

// Adjust for experience level
let experienceMultiplier = 1;
if (experienceLevel.toLowerCase() === 'beginner') {
    experienceMultiplier = 1.8;
} else if (experienceLevel.toLowerCase() === 'intermediate') {
    experienceMultiplier = 1.2;
} else if (experienceLevel.toLowerCase() === 'advanced') {
    experienceMultiplier = 0.7;
}

// Adjust for training frequency (diminishing returns beyond optimal)
let frequencyMultiplier = Math.min(1.5, 0.5 + (trainingSessionsPerWeek * 0.15));
if (trainingSessionsPerWeek > 5) frequencyMultiplier = Math.min(frequencyMultiplier, 1.2); // Cap the benefit for very high frequency

// Adjust for intensity (RPE). Optimal range is 7-9. Too low is ineffective, too high risks overtraining.
let intensityMultiplier = 1;
if (averageRPE >= 7 && averageRPE <= 9) {
    intensityMultiplier = 1 + ((averageRPE - 7) * 0.2); // RPE 7 = 1x, RPE 8 = 1.2x, RPE 9 = 1.4x
} else if (averageRPE < 7) {
    intensityMultiplier = 0.5 + (averageRPE / 10); // Lower RPE, lower multiplier
} else { // RPE > 9, overtraining risk, less effective
    intensityMultiplier = Math.max(0.6, 1.4 - ((averageRPE - 9) * 0.3));
}
intensityMultiplier = Math.max(0.1, Math.min(1.5, intensityMultiplier)); // Cap intensity multiplier

// Adjust for recovery score (crucial for actual gains)
let recoveryMultiplier = Math.max(0.5, recoveryScore / 10); // 10/10 = 1x, 5/10 = 0.5x

// Calculate weekly potential gain percentage
let weeklyGainPercentage = baseGainFactor * experienceMultiplier * frequencyMultiplier * intensityMultiplier * recoveryMultiplier;

// Total projected gain over the cycle
let projectedPercentageGain = weeklyGainPercentage * trainingCycleWeeks * 100; // Convert to actual percentage

// Cap overall gains for realism, as infinite growth isn't possible.
let maxRealisticGain = 0;
if (performanceMetricType.toLowerCase() === 'strength_volume') {
    maxRealisticGain = 20; // Max 20% gain over a cycle for strength/volume
} else if (performanceMetricType.toLowerCase() === 'endurance_vo2max') {
    maxRealisticGain = 12; // Max 12% for VO2max
} else if (performanceMetricType.toLowerCase() === 'endurance_time' || performanceMetricType.toLowerCase() === 'speed_time') {
    maxRealisticGain = 8; // Max 8% reduction for time-based metrics
} else {
    maxRealisticGain = 5; // Default for unknown types
}
projectedPercentageGain = Math.min(projectedPercentageGain, maxRealisticGain);
projectedPercentageGain = Math.max(0, projectedPercentageGain); // No negative gains with this model

let projectedNewPerformanceValue;
if (performanceMetricType.toLowerCase() === 'strength_volume' || performanceMetricType.toLowerCase() === 'endurance_vo2max') {
    projectedNewPerformanceValue = currentPerformanceValue * (1 + (projectedPercentageGain / 100));
} else if (performanceMetricType.toLowerCase() === 'endurance_time' || performanceMetricType.toLowerCase() === 'speed_time') {
    // For time-based metrics, a 'gain' means a reduction in time
    projectedNewPerformanceValue = currentPerformanceValue * (1 - (projectedPercentageGain / 100));
    // Ensure time doesn't go below zero or becomes unrealistic
    projectedNewPerformanceValue = Math.max(0.1, projectedNewPerformanceValue);
} else {
    projectedNewPerformanceValue = currentPerformanceValue; // Default for unhandled types
}

return {
    projectedPercentageGain,
    projectedNewPerformanceValue
};
