const pastControversySeverity = parseFloat(inputs.pastControversySeverity); 
const yearsSinceLastControversy = parseFloat(inputs.yearsSinceLastControversy); 
const socialMediaNegativeMentionsRatio = parseFloat(inputs.socialMediaNegativeMentionsRatio); 
const socialMediaEngagementVolatility = parseFloat(inputs.socialMediaEngagementVolatility); 
const endorsementPortfolioSensitivity = parseFloat(inputs.endorsementPortfolioSensitivity); 
const currentMediaScrutinyIntensity = parseFloat(inputs.currentMediaScrutinyIntensity); 
const athletePublicPerceptionTrend = parseFloat(inputs.athletePublicPerceptionTrend); 
const athleteInfluenceScore = parseFloat(inputs.athleteInfluenceScore); 

// Step 1: Calculate Core Risk Components

// Controversy Impact: Higher severity, more recent = higher impact. Max(0.1, YSC) to avoid division by zero.
const controversyImpact = pastControversySeverity * 12 / Math.max(0.1, yearsSinceLastControversy); 

// Social Media Risk: Ratio of negative mentions combined with volatility.
const socialMediaRisk = (socialMediaNegativeMentionsRatio / 100) * socialMediaEngagementVolatility * 7; 

// Media Scrutiny Contribution: Direct impact of current media attention.
const mediaScrutinyContribution = currentMediaScrutinyIntensity * 9; 

// Step 2: Apply Mitigating/Amplifying Factors

// Perception Trend Adjustment: Positive trend reduces risk, negative increases it.
const perceptionAdjustment = athletePublicPerceptionTrend * -4; // Each +1 trend reduces score by 4

// Influence Multiplier: Higher influence means the impact of risk factors is amplified. 
// If an athlete has low influence, controversies might be contained more easily.
const influenceMultiplier = 1 + (athleteInfluenceScore / 15); // Scales between 1 and approx 1.67

// Step 3: Calculate Base Risk Score

let baseRisk = controversyImpact + socialMediaRisk + mediaScrutinyContribution + perceptionAdjustment; 
baseRisk = Math.max(0, baseRisk); // Ensure base risk is not negative

// Step 4: Calculate Final Brand Risk Score (0-100)

let brandRiskScore = baseRisk * influenceMultiplier; 
brandRiskScore = Math.min(100, Math.max(0, brandRiskScore)); // Cap between 0 and 100

// Step 5: Calculate Public Image Stability Rating (0-10)

// Inversely proportional to brand risk. 10 is most stable, 0 is least.
const imageStabilityRating = 10 - (brandRiskScore / 10); 

// Step 6: Calculate Endorsement Loss Potential (0-100%)

// Combines the overall brand risk with the sensitivity of their endorsement portfolio.
// High brand risk with a highly sensitive portfolio leads to high potential loss.
let endorsementImpactPotential = brandRiskScore * (endorsementPortfolioSensitivity / 10); 
endorsementImpactPotential = Math.min(100, Math.max(0, endorsementImpactPotential)); // Cap between 0 and 100

return {
  brandRiskScore: brandRiskScore,
  imageStabilityRating: imageStabilityRating,
  endorsementImpactPotential: endorsementImpactPotential
};
