const acuteLoad = inputs.trainingDuration * inputs.rpe;

// Normalize HRV, sleep quality, and experience for their impact
const hrvNormalized = Math.max(0.1, inputs.hrvSDNN / 75); // Assuming 75ms is a good baseline, adjust impact
const sleepNormalized = inputs.sleepQuality / 3; // Assuming 3 is average
const experienceNormalized = inputs.athleteExperience / 2; // Assuming 2 is intermediate

// Base optimal load, adjusted by physiological readiness and experience
let calculatedOptimalLoad = acuteLoad * hrvNormalized * sleepNormalized * experienceNormalized;

// Implement an Acute-to-Chronic Workload Ratio (ACWR) adjustment
// Chronic load is roughly previous weekly load divided by 7 days
const chronicLoad = inputs.previousWeeklyLoad / 7;
const acwr = chronicLoad > 0 ? (calculatedOptimalLoad / chronicLoad) : 1;

// Adjust optimal load based on ACWR
// Ideal ACWR is often considered between 0.8 and 1.3
if (acwr < 0.8) {
    calculatedOptimalLoad *= 0.85; // Reduce load if acute load is too low relative to chronic
} else if (acwr > 1.3) {
    calculatedOptimalLoad *= 0.75; // Significantly reduce load if acute load is too high (injury risk)
}

// Cap optimal training load to reasonable min/max values
const optimalTrainingLoad = Math.round(Math.max(50, Math.min(calculatedOptimalLoad, 6000)));

// Recovery Time Calculation
let recoveryHours = 12; // Base recovery hours

// Add recovery based on optimal load
recoveryHours += optimalTrainingLoad / 200; // Each 200 load units adds 1 hour

// Adjust recovery based on sleep quality (poor sleep adds more recovery)
recoveryHours += (5 - inputs.sleepQuality) * 3; // 1 point decrease in sleep quality adds 3 hours

// Adjust recovery based on HRV (lower HRV adds more recovery)
recoveryHours += (75 - inputs.hrvSDNN) * 0.2; // Each 1ms decrease below 75 adds 0.2 hours

// Apply minimum and maximum caps for recovery time
const recoveryTimeHours = Math.round(Math.max(8, Math.min(recoveryHours, 72)));

return {
  optimalTrainingLoad,
  recoveryTimeHours
};
