function calculate({
  flow_rate_gpm,
  system_inlet_pressure_psi,
  backflow_preventer_type,
  nominal_pipe_size_in,
  elevation_change_downstream_ft,
  downstream_pipe_length_ft
}) {
  const PSI_PER_FOOT_WATER = 0.433;
  const DEFAULT_PIPE_DIAMETER = 1;

  // Normalize input strings and ensure positive pipe size
  const type = backflow_preventer_type.toUpperCase().trim();
  const pipe_size = nominal_pipe_size_in > 0 ? nominal_pipe_size_in : DEFAULT_PIPE_DIAMETER;

  let backflow_k_factor = 0;

  // Simplified K-factors (for DeltaP = K * Q^2, where Q is GPM)
  // These are illustrative and not from specific manufacturer data.
  // RPZ generally has highest loss, PVB lowest.
  if (type === 'RPZ') {
    if (pipe_size <= 1) backflow_k_factor = 0.005;
    else if (pipe_size <= 2) backflow_k_factor = 0.0015;
    else if (pipe_size <= 4) backflow_k_factor = 0.0004;
    else if (pipe_size <= 6) backflow_k_factor = 0.00015;
    else backflow_k_factor = 0.00008; // For larger sizes
  } else if (type === 'DCVA') {
    if (pipe_size <= 1) backflow_k_factor = 0.002;
    else if (pipe_size <= 2) backflow_k_factor = 0.0005;
    else if (pipe_size <= 4) backflow_k_factor = 0.0001;
    else if (pipe_size <= 6) backflow_k_factor = 0.00004;
    else backflow_k_factor = 0.00002; // For larger sizes
  } else if (type === 'PVB') {
    if (pipe_size <= 1) backflow_k_factor = 0.001;
    else if (pipe_size <= 2) backflow_k_factor = 0.0003;
    else if (pipe_size <= 4) backflow_k_factor = 0.00006;
    else if (pipe_size <= 6) backflow_k_factor = 0.00002;
    else backflow_k_factor = 0.00001; // For larger sizes
  } else {
    // Default for unsupported types, or log a warning in a real system
    backflow_k_factor = 0;
  }

  // Calculate backflow preventer pressure loss
  let backflow_preventer_pressure_loss_psi = backflow_k_factor * Math.pow(flow_rate_gpm, 2);
  // Cap backflow loss to prevent unrealistic results for extreme inputs
  backflow_preventer_pressure_loss_psi = Math.min(backflow_preventer_pressure_loss_psi, system_inlet_pressure_psi * 0.9);
  backflow_preventer_pressure_loss_psi = Math.max(0, backflow_preventer_pressure_loss_psi); // Ensure non-negative

  // Calculate elevation pressure loss
  const elevation_pressure_loss_psi = elevation_change_downstream_ft * PSI_PER_FOOT_WATER;

  // Simplified Darcy-Weisbach or Hazen-Williams like approximation for downstream pipe friction loss
  // friction_constant is an arbitrary value scaled for PSI/GPM/FT/INCH^5, highly simplified
  const friction_constant = 0.0000005; 
  let downstream_pipe_friction_loss_psi = friction_constant * Math.pow(flow_rate_gpm, 2) * downstream_pipe_length_ft / Math.pow(pipe_size, 5); 

  // Cap pipe friction loss similarly
  downstream_pipe_friction_loss_psi = Math.min(downstream_pipe_friction_loss_psi, system_inlet_pressure_psi * 0.7);
  downstream_pipe_friction_loss_psi = Math.max(0, downstream_pipe_friction_loss_psi); // Ensure non-negative

  // Calculate total system pressure loss
  const total_system_pressure_loss_psi = backflow_preventer_pressure_loss_psi + downstream_pipe_friction_loss_psi + elevation_pressure_loss_psi;

  // Calculate available pressure downstream
  const available_pressure_downstream_psi = Math.max(0, system_inlet_pressure_psi - total_system_pressure_loss_psi);

  return {
    backflow_preventer_pressure_loss_psi,
    downstream_pipe_friction_loss_psi,
    total_system_pressure_loss_psi,
    available_pressure_downstream_psi
  };
}
