const batteryCapacityAh = inputs.batteryCapacityAh; 
const batteryVoltageV = inputs.batteryVoltageV; 
const totalLoadW = inputs.totalLoadW; 
const inverterEfficiencyPct = inputs.inverterEfficiencyPct; 
const dodPct = inputs.dodPct; 

// Convert percentages to decimals, capping DoD at 100% and efficiency at 100% 
const inverterEfficiencyDecimal = Math.min(inverterEfficiencyPct / 100, 1); 
const dodDecimal = Math.min(dodPct / 100, 1); 

// 1. Calculate total battery bank energy in Watt-hours (Wh) 
const totalBatteryEnergyWh = batteryCapacityAh * batteryVoltageV; 

// 2. Calculate usable battery energy in Wh based on DoD 
const usableBatteryEnergyWh = totalBatteryEnergyWh * dodDecimal; 

// 3. Calculate actual power drawn from the battery, accounting for inverter losses 
// Prevent division by zero if efficiency is 0 or very low, default to a high penalty 
let powerDrawnFromBatteryW = 0; 
if (inverterEfficiencyDecimal > 0) { 
  powerDrawnFromBatteryW = totalLoadW / inverterEfficiencyDecimal; 
} else { 
  powerDrawnFromBatteryW = totalLoadW * 10; // Penalize severely for 0 efficiency 
} 

// Prevent division by zero if powerDrawnFromBatteryW is 0 (i.e., totalLoadW is 0) 
let runtimeHours = 0; 
if (powerDrawnFromBatteryW > 0 && usableBatteryEnergyWh > 0) { 
  runtimeHours = usableBatteryEnergyWh / powerDrawnFromBatteryW; 
} else if (totalLoadW === 0 && usableBatteryEnergyWh > 0) { 
  runtimeHours = Infinity; // If no load, and there is usable energy, runtime is theoretically infinite 
} 

const runtimeDays = runtimeHours === Infinity ? Infinity : runtimeHours / 24; 

return { 
  availableEnergyWh: usableBatteryEnergyWh, 
  runtimeHours: runtimeHours, 
  runtimeDays: runtimeDays 
};
