formula:
/**
 * Calculates the deflection of a simply supported, uniformly loaded wood joist.
 * Formula: Δ = (5 * w * L^4) / (384 * E * I)
 * where:
 * Δ = deflection (inches)
 * w = uniform load (pounds per linear inch, pli)
 * L = span length (inches)
 * E = Modulus of Elasticity (psi)
 * I = Moment of Inertia (in^4)
 */

const L_ft = inputs.span_length;
const b_in = inputs.joist_width;
const h_in = inputs.joist_height;
const E_psi = inputs.modulus_elasticity;
const LL_psf = inputs.live_load;
const DL_psf = inputs.dead_load;
const spacing_in = inputs.joist_spacing;

// 1. Convert span to inches
const L_in = L_ft * 12;

// 2. Calculate Moment of Inertia (I) for a rectangular section
// I = (b * h^3) / 12
const I_in4 = (b_in * Math.pow(h_in, 3)) / 12;

// 3. Calculate total uniform load per linear inch (w_pli)
// Total area load (psf)
const total_load_psf = LL_psf + DL_psf;
// Load per linear foot (plf) = total_load_psf * (joist_spacing / 12)
const w_plf = total_load_psf * (spacing_in / 12);
// Load per linear inch (pli) = w_plf / 12
const w_pli = w_plf / 12;

// 4. Calculate deflection (Δ) in inches
const calculated_deflection = (5 * w_pli * Math.pow(L_in, 4)) / (384 * E_psi * I_in4);

// 5. Calculate deflection ratio (L/X)
let deflection_ratio_val = Infinity;
if (calculated_deflection > 0) {
  deflection_ratio_val = L_in / calculated_deflection;
}
const deflection_ratio = Math.round(Math.min(deflection_ratio_val, 9999)); // Cap at L/9999 for display

// 6. Determine typical allowable deflection (e.g., L/360 for floors)
const allowable_deflection = L_in / 360;

// 7. Estimate code compliance
let code_compliance_estimate = 'N/A';
if (!isNaN(calculated_deflection) && calculated_deflection > 0) {
  if (calculated_deflection <= allowable_deflection) {
    code_compliance_estimate = 'Likely Meets L/360 (Good)';
  } else {
    code_compliance_estimate = 'Exceeds L/360 (Consider Redesign)';
  }
} else if (calculated_deflection <= 0) {
    code_compliance_estimate = 'No Deflection (Verify Inputs)';
}

return {
  calculated_deflection: calculated_deflection,
  deflection_ratio: deflection_ratio,
  allowable_deflection: allowable_deflection,
  code_compliance_estimate: code_compliance_estimate
};
