function calculate({ initialImportVolume, initialExportVolume, currentTariffRate, proposedTariffChange, priceElasticityOfDemand, industrySensitivityFactor }) {
    // Input validation (basic checks for non-negativity and sensible ranges)
    if (initialImportVolume < 0 || initialExportVolume < 0 || currentTariffRate < 0 || priceElasticityOfDemand <= 0 || industrySensitivityFactor <= 0) {
        throw new Error("All volumes, rates, elasticity, and sensitivity factors must be positive or zero where applicable.");
    }

    // 1. Calculate New Effective Tariff Rate
    let newTariffRate = currentTariffRate + proposedTariffChange;
    newTariffRate = Math.max(0, Math.min(100, newTariffRate)); // Cap between 0% and 100%

    // 2. Calculate Tariff Change Factor and Price Impact
    const tariffChangeAsDecimal = proposedTariffChange / 100; // e.g., +5% change is 0.05, -5% is -0.05

    // Assume the tariff change directly impacts the price of imported goods by that percentage.
    // Note: For simplicity, we assume tariffs are fully passed through to prices. In reality, incidence can be shared.
    const effectivePriceChangeFactor = tariffChangeAsDecimal;

    // 3. Estimate Change in Import Volume (using Price Elasticity of Demand)
    // % Change in Quantity = -Price Elasticity of Demand * % Change in Price
    // We use the absolute value of elasticity as convention, so apply negative sign for direct price changes.
    const changeInImportVolumePercentage = -priceElasticityOfDemand * effectivePriceChangeFactor;
    const estimatedImportChange = initialImportVolume * changeInImportVolumePercentage;
    let newImportVolume = initialImportVolume + estimatedImportChange;
    newImportVolume = Math.max(0, newImportVolume); // Imports cannot be negative

    // 4. Calculate Government Tariff Revenue Impact (Nation A)
    const governmentRevenueBefore = initialImportVolume * (currentTariffRate / 100);
    const governmentRevenueAfter = newImportVolume * (newTariffRate / 100);
    const governmentRevenueImpact = governmentRevenueAfter - governmentRevenueBefore;

    // 5. Calculate Consumer Cost Impact (Nation A)
    // Represents the estimated change in total expenditure by consumers/businesses in Nation A
    // on imports from Nation B, accounting for both price and volume changes.
    const oldAveragePriceFactor = 1 + (currentTariffRate / 100);
    const newAveragePriceFactor = 1 + (newTariffRate / 100);
    const oldTotalImportExpenditure = initialImportVolume * oldAveragePriceFactor;
    const newTotalImportExpenditure = newImportVolume * newAveragePriceFactor;
    const consumerCostImpact = newTotalImportExpenditure - oldTotalImportExpenditure;

    // 6. Estimate Change in Export Volume (Nation A to Nation B)
    // This is an estimation of reciprocal impact or general trade friction based on
    // Nation A's proposed tariff change and the industry's sensitivity.
    // A higher absolute tariff change by A is assumed to negatively impact A's exports to B.
    const exportRetaliationCoefficient = 0.5; // Arbitrary factor for B's potential reaction magnitude.
    const estimatedExportChange = initialExportVolume * (-(Math.abs(proposedTariffChange) / 100) * exportRetaliationCoefficient * industrySensitivityFactor);
    const finalExportVolume = initialExportVolume + estimatedExportChange; 
    // Note: Export volume could theoretically fall to zero but not become negative in this model.
    // We allow `estimatedExportChange` to be a large negative for impact calculation, but a 'finalExportVolume' if needed would be Math.max(0, finalExportVolume).

    // 7. Calculate Net Trade Balance Impact (Nation A)
    // Trade Balance = Exports - Imports. Impact is the change in this balance.
    // (New Exports - New Imports) - (Old Exports - Old Imports)
    const netTradeBalanceImpact = (finalExportVolume - newImportVolume) - (initialExportVolume - initialImportVolume);

    // 8. Calculate Industry Revenue Impact (Nation A)
    // This models the net effect on Nation A's domestic industries.
    // If imports decrease (estimatedImportChange is negative), domestic competitors generally benefit.
    // If exports decrease (estimatedExportChange is negative), domestic exporters face revenue loss.
    const domesticCompetitorBenefit = (estimatedImportChange < 0) ? -estimatedImportChange : 0; // If imports decrease, a 'positive' for competitors.
    const domesticExporterLoss = (estimatedExportChange < 0) ? -estimatedExportChange : 0; // If exports decrease, it's a 'loss' for exporters.

    // Weighting: Assume import-competing industries are more sensitive (e.g., 70%) than export-oriented ones (e.g., 30%) 
    // to the *overall trade friction* as represented by the sensitivity factor.
    // The industry sensitivity factor scales the overall impact.
    const industryRevenueImpact = (domesticCompetitorBenefit * 0.7 - domesticExporterLoss * 0.3) * industrySensitivityFactor;

    return {
        newTariffRate: newTariffRate,
        estimatedImportChange: estimatedImportChange,
        estimatedExportChange: estimatedExportChange,
        consumerCostImpact: consumerCostImpact,
        governmentRevenueImpact: governmentRevenueImpact,
        netTradeBalanceImpact: netTradeBalanceImpact,
        industryRevenueImpact: industryRevenueImpact
    };
}
