const S = parseFloat(inputs.stockPrice) || 0;
const K = parseFloat(inputs.strikePrice) || 0;
const T = parseFloat(inputs.timeToExpiration) || 0;
const r = (parseFloat(inputs.riskFreeRate) || 0) / 100;
const sigma = (parseFloat(inputs.volatility) || 0) / 100;

if (T <= 0 || sigma <= 0 || S <= 0 || K <= 0) return { callPrice: 0, putPrice: 0, d1: 0 };

const d1 = (Math.log(S / K) + (r + (sigma * sigma) / 2) * T) / (sigma * Math.sqrt(T));
const d2 = d1 - sigma * Math.sqrt(T);

// CDF function
function cdf(x) {
    const a1 = 0.319381530;
    const a2 = -0.356563782;
    const a3 = 1.781477937;
    const a4 = -1.821255978;
    const a5 = 1.330274429;
    const L = Math.abs(x);
    const k = 1.0 / (1.0 + 0.2316419 * L);
    const w = 1.0 - 1.0 / Math.sqrt(2 * Math.PI) * Math.exp(-L * L / 2) * (a1 * k + a2 * k * k + a3 * k * k * k + a4 * k * k * k * k + a5 * k * k * k * k * k);
    return x < 0 ? 1.0 - w : w;
}

const call = S * cdf(d1) - K * Math.exp(-r * T) * cdf(d2);
const put = K * Math.exp(-r * T) * cdf(-d2) - S * cdf(-d1);

return {
    callPrice: Math.max(0, call),
    putPrice: Math.max(0, put),
    d1: d1
};
