const weight = inputs.weight;
const heightCm = inputs.height;

if (!weight || !heightCm || weight <= 0 || heightCm <= 0) {
  return { bmi: 0, category: 'Invalid Input', healthy_range: '-' };
}

const heightM = heightCm / 100;
const bmi = weight / (heightM * heightM);

let category = '';
if (bmi < 18.5) category = 'Underweight';
else if (bmi < 25) category = 'Normal Weight';
else if (bmi < 30) category = 'Overweight';
else if (bmi < 35) category = 'Obesity Class I';
else if (bmi < 40) category = 'Obesity Class II';
else category = 'Obesity Class III (Severe)';

const minHealthy = 18.5 * heightM * heightM;
const maxHealthy = 24.9 * heightM * heightM;

return {
  bmi,
  category,
  healthy_range: `${minHealthy.toFixed(1)} - ${maxHealthy.toFixed(1)}`
};
