const { parValue, couponRate, currentPrice, yearsToMaturity, couponFrequency } = inputs;

const couponPaymentPerPeriod = (parValue * couponRate) / couponFrequency;
const totalPeriods = yearsToMaturity * couponFrequency;

// Function to calculate Present Value (PV) of a bond for a given yield rate
const calculateBondPV = (rate) => {
  if (rate <= 0) { // Handle zero or negative rates to prevent errors, return a very high value to push rate up if needed
    return couponPaymentPerPeriod * totalPeriods + parValue;
  }
  // Present value of an annuity (coupon payments)
  const pvCoupons = couponPaymentPerPeriod * (1 - Math.pow(1 + rate, -totalPeriods)) / rate;
  // Present value of the par value at maturity
  const pvPar = parValue / Math.pow(1 + rate, totalPeriods);
  return pvCoupons + pvPar;
};

let low = 0.0001; // Lower bound for yield per period (avoiding 0 for division)
let high = 1.0; // Upper bound for yield per period (e.g., 100%) - can be adjusted
let ytmPeriod = 0;

// Bisection method to find the YTM (yield per period)
for (let i = 0; i < 200; i++) { // Max iterations for convergence
  let mid = (low + high) / 2;
  if (mid === 0) { // Avoid division by zero
      low = 0.0000001; // Adjust low bound slightly
      continue;
  }
  const pvCalculated = calculateBondPV(mid);

  if (pvCalculated > currentPrice) {
    low = mid; // If calculated PV is higher than current price, the rate is too low, increase it
  } else {
    high = mid; // If calculated PV is lower than current price, the rate is too high, decrease it
  }

  if (Math.abs(pvCalculated - currentPrice) < 0.000001) { // Convergence check: if PV is close enough
    ytmPeriod = mid;
    break;
  }
  ytmPeriod = mid; // Store current best estimate
}

// Annualize the YTM (Yield to Maturity)
const yieldToMaturity = ytmPeriod * couponFrequency * 100; // Convert to percentage

// Calculate Effective Annual Yield (EAY)
const effectiveAnnualYield = (Math.pow(1 + ytmPeriod, couponFrequency) - 1) * 100; // Convert to percentage

return {
  yieldToMaturity: yieldToMaturity,
  effectiveAnnualYield: effectiveAnnualYield
};
