const currentPrice = inputs.currentPrice;
const faceValue = inputs.faceValue;
const annualCouponRate = inputs.couponRate / 100; // Convert percentage to decimal
const yearsToMaturity = inputs.yearsToMaturity;
const couponFrequency = inputs.couponFrequency;

const optionType = inputs.optionType ? inputs.optionType.toLowerCase() : 'none';
const optionPrice = inputs.optionPrice;
const yearsToOption = inputs.yearsToOption;

const results = {};

const annualCouponPayment = annualCouponRate * faceValue;

// --- YTM Calculation (always calculated) ---
if (yearsToMaturity > 0 && currentPrice > 0) {
    const ytmApprox = (annualCouponPayment + (faceValue - currentPrice) / yearsToMaturity) / ((faceValue + currentPrice) / 2);
    results.ytm = ytmApprox * 100; // Convert to percentage
} else {
    results.ytm = NaN;
}

// --- YTC Calculation ---
if (optionType === "call" && optionPrice && yearsToOption > 0 && currentPrice > 0) {
    const ytcApprox = (annualCouponPayment + (optionPrice - currentPrice) / yearsToOption) / ((optionPrice + currentPrice) / 2);
    results.ytc = ytcApprox * 100; // Convert to percentage
} else {
    results.ytc = NaN;
}

// --- YTP Calculation ---
if (optionType === "put" && optionPrice && yearsToOption > 0 && currentPrice > 0) {
    const ytpApprox = (annualCouponPayment + (optionPrice - currentPrice) / yearsToOption) / ((optionPrice + currentPrice) / 2);
    results.ytp = ytpApprox * 100; // Convert to percentage
} else {
    results.ytp = NaN;
}

return results;
