function calculateROI(inputs) {
    const {
        initialRetrofitCost,
        currentAnnualEnergyCost,
        expectedEnergyReductionPercentage,
        governmentIncentives,
        annualMaintenanceSavings,
        projectLifespan,
        annualEnergyInflationRate,
        annualDiscountRate
    } = inputs;

    // --- Input Validation & Edge Cases ---
    if (initialRetrofitCost < 0 || currentAnnualEnergyCost < 0 || expectedEnergyReductionPercentage < 0 || governmentIncentives < 0 || annualMaintenanceSavings < 0) {
        throw new Error("Input values for cost, savings, or incentives cannot be negative.");
    }
    if (expectedEnergyReductionPercentage > 100) {
        throw new Error("Energy reduction percentage cannot exceed 100%.");
    }
    if (projectLifespan <= 0) {
        // Handle zero or negative lifespan as immediate cost with no savings
        const netInvest = initialRetrofitCost - governmentIncentives;
        return {
            netInitialInvestment: netInvest,
            firstYearNetSavings: 0,
            simplePaybackPeriod: Infinity,
            totalLifetimeUndiscountedSavings: 0,
            netPresentValue: -netInvest, 
            totalLifetimeROI: (netInvest === 0 ? 0 : -100) // If no investment, ROI is 0; otherwise -100% for losing initial capital
        };
    }

    const netInitialInvestment = initialRetrofitCost - governmentIncentives;
    const energyInflationFactor = 1 + (annualEnergyInflationRate / 100);
    const discountFactor = 1 + (annualDiscountRate / 100);

    const baseAnnualEnergySavings = currentAnnualEnergyCost * (expectedEnergyReductionPercentage / 100);
    const firstYearNetSavings = baseAnnualEnergySavings + annualMaintenanceSavings;

    // --- Simple Payback Period ---
    let simplePaybackPeriod = Infinity;
    if (firstYearNetSavings > 0) {
        simplePaybackPeriod = netInitialInvestment / firstYearNetSavings;
        if (simplePaybackPeriod < 0) simplePaybackPeriod = 0; // If net initial investment is negative (incentives > cost) and savings exist, payback is instant.
    } else if (netInitialInvestment <= 0) {
        simplePaybackPeriod = 0; // If no initial investment and no savings, payback is 0.
    }
    // If savings are 0 or negative, and initial investment is positive, payback is effectively infinite.

    // --- Lifetime Savings & NPV Calculation ---
    let totalLifetimeUndiscountedSavings = 0;
    let netPresentValue = -netInitialInvestment; // Start NPV with initial outlay

    let currentYearEnergySavings = baseAnnualEnergySavings; // This will inflate year by year
    for (let year = 1; year <= projectLifespan; year++) {
        // Adjust energy savings for inflation from Year 2 onwards
        if (year > 1) {
            currentYearEnergySavings *= energyInflationFactor;
        }

        const annualCashInflow = currentYearEnergySavings + annualMaintenanceSavings;
        totalLifetimeUndiscountedSavings += annualCashInflow;

        // Calculate discounted cash flow for NPV
        netPresentValue += annualCashInflow / Math.pow(discountFactor, year);
    }

    // --- Total Lifetime ROI (Undiscounted) ---
    let totalLifetimeROI = 0;
    if (netInitialInvestment !== 0) {
        totalLifetimeROI = ((totalLifetimeUndiscountedSavings - netInitialInvestment) / netInitialInvestment) * 100;
    } else {
        // If no net initial investment, and there are savings, ROI is infinite. If no savings, 0.
        totalLifetimeROI = (totalLifetimeUndiscountedSavings > 0) ? Infinity : 0;
    }

    return {
        netInitialInvestment: netInitialInvestment,
        firstYearNetSavings: firstYearNetSavings,
        simplePaybackPeriod: simplePaybackPeriod,
        totalLifetimeUndiscountedSavings: totalLifetimeUndiscountedSavings,
        netPresentValue: netPresentValue,
        totalLifetimeROI: totalLifetimeROI
    };
}
