const wall_r_materials = parseFloat(inputs.wall_assembly_rvalue);
const roof_r_materials = parseFloat(inputs.roof_assembly_rvalue);
const floor_r_materials = parseFloat(inputs.floor_assembly_rvalue);
const int_air_film_rvalue = parseFloat(inputs.int_air_film_rvalue);
const wall_ext_air_film_rvalue = parseFloat(inputs.wall_ext_air_film_rvalue);

// Standard exterior air film resistances for roof and floor based on common conditions (ASHRAE Handbook)
// Roof: Upward heat flow, average exposure (e.g., 15 mph wind)
const roof_ext_air_film_rvalue_default = 0.25;
// Floor: Downward heat flow, still air (e.g., floor over unheated space/crawl space)
const floor_ext_air_film_rvalue_default = 0.82;

// Calculate total R-values for each assembly
const overall_wall_rvalue = wall_r_materials + int_air_film_rvalue + wall_ext_air_film_rvalue;
const overall_roof_rvalue = roof_r_materials + int_air_film_rvalue + roof_ext_air_film_rvalue_default;
const overall_floor_rvalue = floor_r_materials + int_air_film_rvalue + floor_ext_air_film_rvalue_default;

// Ensure R-values are positive to prevent division by zero or negative results for U-factor
const minR = 0.001; // A very small positive number to prevent invalid U-factor calculation

// Calculate U-factors (U = 1/R)
const overall_wall_ufactor = 1 / Math.max(overall_wall_rvalue, minR);
const overall_roof_ufactor = 1 / Math.max(overall_roof_rvalue, minR);
const overall_floor_ufactor = 1 / Math.max(overall_floor_rvalue, minR);

return {
    overall_wall_rvalue,
    overall_wall_ufactor,
    overall_roof_rvalue,
    overall_roof_ufactor,
    overall_floor_rvalue,
    overall_floor_ufactor
};
