function calculateLCC({ initialCapitalCost, projectLifespan, annualOMCost, majorRenovationCost, majorRenovationFrequency, decommissioningCost, salvageValue, inflationRate, discountRate }) {
  initialCapitalCost = Number(initialCapitalCost);
  projectLifespan = Number(projectLifespan);
  annualOMCost = Number(annualOMCost);
  majorRenovationCost = Number(majorRenovationCost);
  majorRenovationFrequency = Number(majorRenovationFrequency);
  decommissioningCost = Number(decommissioningCost);
  salvageValue = Number(salvageValue);
  inflationRate = Number(inflationRate) / 100;
  discountRate = Number(discountRate) / 100;

  let totalLifecycleCost = initialCapitalCost;
  let netPresentValueSum = initialCapitalCost;

  if (projectLifespan <= 0) {
    return {
      totalLifecycleCost: initialCapitalCost,
      netPresentValue: initialCapitalCost,
      averageAnnualizedCost: initialCapitalCost
    };
  }

  for (let year = 1; year <= projectLifespan; year++) {
    // 1. Inflated Annual Operating & Maintenance Costs
    const inflatedOMCost = annualOMCost * Math.pow(1 + inflationRate, year);
    totalLifecycleCost += inflatedOMCost;
    netPresentValueSum += inflatedOMCost / Math.pow(1 + discountRate, year);

    // 2. Inflated Major Renovation/Replacement Costs
    if (majorRenovationFrequency > 0 && year % majorRenovationFrequency === 0) {
      const inflatedRenovationCost = majorRenovationCost * Math.pow(1 + inflationRate, year);
      totalLifecycleCost += inflatedRenovationCost;
      netPresentValueSum += inflatedRenovationCost / Math.pow(1 + discountRate, year);
    }
  }

  // 3. End-of-Life Costs (Decommissioning & Salvage Value) at the end of the project lifespan
  const inflatedDecommissioningCost = decommissioningCost * Math.pow(1 + inflationRate, projectLifespan);
  totalLifecycleCost += inflatedDecommissioningCost;
  netPresentValueSum += inflatedDecommissioningCost / Math.pow(1 + discountRate, projectLifespan);

  const inflatedSalvageValue = salvageValue * Math.pow(1 + inflationRate, projectLifespan);
  totalLifecycleCost -= inflatedSalvageValue; // Salvage value reduces total cost
  netPresentValueSum -= inflatedSalvageValue / Math.pow(1 + discountRate, projectLifespan); // Salvage value reduces NPV

  const averageAnnualizedCost = totalLifecycleCost / projectLifespan;

  return {
    totalLifecycleCost: totalLifecycleCost,
    netPresentValue: netPresentValueSum,
    averageAnnualizedCost: averageAnnualizedCost
  };
}
