function calculate({ concreteVolume, concreteECF, steelMass, steelECF, timberVolume, timberECF, insulationVolume, insulationECF, otherMaterialMass, otherMaterialECF, averageTransportDistance, transportModeFactor }) {
  // Input validation and default to 0 if not valid number
  concreteVolume = parseFloat(concreteVolume) || 0;
  concreteECF = parseFloat(concreteECF) || 0;
  steelMass = parseFloat(steelMass) || 0;
  steelECF = parseFloat(steelECF) || 0;
  timberVolume = parseFloat(timberVolume) || 0;
  timberECF = parseFloat(timberECF) || 0;
  insulationVolume = parseFloat(insulationVolume) || 0;
  insulationECF = parseFloat(insulationECF) || 0;
  otherMaterialMass = parseFloat(otherMaterialMass) || 0;
  otherMaterialECF = parseFloat(otherMaterialECF) || 0;
  averageTransportDistance = parseFloat(averageTransportDistance) || 0;
  transportModeFactor = parseFloat(transportModeFactor) || 0;

  // Ensure non-negative inputs
  concreteVolume = Math.max(0, concreteVolume);
  concreteECF = Math.max(0, concreteECF);
  steelMass = Math.max(0, steelMass);
  steelECF = Math.max(0, steelECF);
  timberVolume = Math.max(0, timberVolume);
  timberECF = Math.max(0, timberECF);
  insulationVolume = Math.max(0, insulationVolume);
  insulationECF = Math.max(0, insulationECF);
  otherMaterialMass = Math.max(0, otherMaterialMass);
  otherMaterialECF = Math.max(0, otherMaterialECF);
  averageTransportDistance = Math.max(0, averageTransportDistance);
  transportModeFactor = Math.max(0, transportModeFactor);

  // Assume standard densities for volume-based materials to estimate mass for transport
  const concreteDensity = 2400; // kg/m³
  const timberDensity = 500;    // kg/m³ (average for softwood)
  const insulationDensity = 30; // kg/m³ (average for mineral wool/fiberglass)

  // 1. Calculate Material Embodied Carbon (A1-A3 + A5 - direct installation emissions, not transport)
  const concreteCarbon = concreteVolume * concreteECF;
  const steelCarbon = (steelMass / 1000) * steelECF; // Steel ECF is per tonne
  const timberCarbon = timberVolume * timberECF;
  const insulationCarbon = insulationVolume * insulationECF;
  const otherMaterialCarbon = (otherMaterialMass / 1000) * otherMaterialECF; // Other ECF is per tonne

  const materialEmbodiedCarbon = concreteCarbon + steelCarbon + timberCarbon + insulationCarbon + otherMaterialCarbon;

  // 2. Estimate total material mass for transportation
  const concreteMassForTransport = concreteVolume * concreteDensity;
  const timberMassForTransport = timberVolume * timberDensity;
  const insulationMassForTransport = insulationVolume * insulationDensity;

  // Total mass in kg for transport calculation (steel, other materials already in kg)
  const totalMass_kg = concreteMassForTransport + steelMass + timberMassForTransport + insulationMassForTransport + otherMaterialMass;
  const totalMass_tonnes = totalMass_kg / 1000;

  // 3. Calculate Transportation Embodied Carbon (A4)
  // Assuming a round trip or one-way factor already accounts for return empty/full logic
  // For simplicity, we use one-way distance * 2 if we assume materials are transported once and the transport vehicle returns.
  // Given the input is 'average one-way transport distance', we should clarify if it's supply chain distance or total site distance.
  // Let's assume 'average one-way' refers to the distance goods travel from a manufacturing site to the construction site.
  // A common approach for A4 is to consider the distance for the material delivery.
  const transportEmbodiedCarbon = totalMass_tonnes * averageTransportDistance * transportModeFactor;

  // 4. Calculate Total Embodied Carbon (A1-A5)
  const totalEmbodiedCarbon = materialEmbodiedCarbon + transportEmbodiedCarbon;

  // Handle cases where total carbon might be negative due to erroneous ECFs (shouldn't happen with valid ECFs, but for robustness)
  const finalTotalEmbodiedCarbon = Math.max(0, totalEmbodiedCarbon);
  const finalMaterialEmbodiedCarbon = Math.max(0, materialEmbodiedCarbon);
  const finalTransportEmbodiedCarbon = Math.max(0, transportEmbodiedCarbon);

  return {
    totalEmbodiedCarbon: finalTotalEmbodiedCarbon,
    materialEmbodiedCarbon: finalMaterialEmbodiedCarbon,
    transportEmbodiedCarbon: finalTransportEmbodiedCarbon
  };
}
