function calculateROI(inputs) {
  const buildingValue = inputs.buildingValue || 0;
  const retrofitCost = inputs.retrofitCost || 0;
  const estimatedDamagePercentageNoRetrofit = inputs.estimatedDamagePercentageNoRetrofit || 0;
  const estimatedDamagePercentageWithRetrofit = inputs.estimatedDamagePercentageWithRetrofit || 0;
  const annualSeismicEventProbability = inputs.annualSeismicEventProbability || 0;
  const annualInsurancePremiumBefore = inputs.annualInsurancePremiumBefore || 0;
  const insurancePremiumReductionPercentage = inputs.insurancePremiumReductionPercentage || 0;
  const analysisPeriodYears = inputs.analysisPeriodYears || 1;
  const discountRate = inputs.discountRate || 0;

  // Input validation and caps
  if (buildingValue <= 0 || retrofitCost < 0 || analysisPeriodYears <= 0) {
    return {
      totalRetrofitCost: retrofitCost,
      annualAvoidedDamageCost: 0,
      annualInsurancePremiumSavings: 0,
      totalAnnualBenefit: 0,
      netPresentValue: -retrofitCost,
      simplePaybackPeriodYears: 'N/A'
    };
  }

  const actualEstimatedDamagePercentageNoRetrofit = Math.min(Math.max(0, estimatedDamagePercentageNoRetrofit), 100);
  const actualEstimatedDamagePercentageWithRetrofit = Math.min(Math.max(0, estimatedDamagePercentageWithRetrofit), 100);
  const actualAnnualSeismicEventProbability = Math.min(Math.max(0, annualSeismicEventProbability), 100);
  const actualInsurancePremiumReductionPercentage = Math.min(Math.max(0, insurancePremiumReductionPercentage), 100);
  const actualDiscountRate = Math.max(0, discountRate);

  // 1. Calculate Annual Avoided Damage Cost
  const potentialDamageNoRetrofit = buildingValue * (actualEstimatedDamagePercentageNoRetrofit / 100);
  const potentialDamageWithRetrofit = buildingValue * (actualEstimatedDamagePercentageWithRetrofit / 100);
  const damageReducedPerEvent = Math.max(0, potentialDamageNoRetrofit - potentialDamageWithRetrofit);
  const annualAvoidedDamageCost = damageReducedPerEvent * (actualAnnualSeismicEventProbability / 100);

  // 2. Calculate Annual Insurance Premium Savings
  const annualInsurancePremiumSavings = annualInsurancePremiumBefore * (actualInsurancePremiumReductionPercentage / 100);

  // 3. Calculate Total Annual Benefit
  const totalAnnualBenefit = annualAvoidedDamageCost + annualInsurancePremiumSavings;

  // 4. Calculate Net Present Value (NPV)
  let totalDiscountedBenefits = 0;
  const rate = actualDiscountRate / 100;

  for (let i = 1; i <= analysisPeriodYears; i++) {
    totalDiscountedBenefits += totalAnnualBenefit / Math.pow(1 + rate, i);
  }
  const netPresentValue = totalDiscountedBenefits - retrofitCost;

  // 5. Calculate Simple Payback Period
  let simplePaybackPeriodYears = 'N/A';
  if (totalAnnualBenefit > 0) {
    const payback = retrofitCost / totalAnnualBenefit;
    simplePaybackPeriodYears = parseFloat(payback.toFixed(1));
    if (payback > analysisPeriodYears) {
        simplePaybackPeriodYears = `>${analysisPeriodYears}`;
    }
  } else if (retrofitCost === 0) {
      simplePaybackPeriodYears = 0;
  }

  return {
    totalRetrofitCost: retrofitCost,
    annualAvoidedDamageCost: annualAvoidedDamageCost,
    annualInsurancePremiumSavings: annualInsurancePremiumSavings,
    totalAnnualBenefit: totalAnnualBenefit,
    netPresentValue: netPresentValue,
    simplePaybackPeriodYears: simplePaybackPeriodYears
  };
}
