const building_height_m = inputs.building_height_m;
const building_width_m = inputs.building_width_m;
const design_wind_speed_kph = inputs.design_wind_speed_kph;
const terrain_category = inputs.terrain_category.toLowerCase();
const building_occupancy_category = inputs.building_occupancy_category.toUpperCase();
const structural_resilience_factor = inputs.structural_resilience_factor;

// Constants
const air_density_kg_m3 = 1.225; // Air density at sea level (kg/m^3)
const gust_effect_factor = 0.85; // Simplified gust effect factor
const pressure_coefficient = 0.8; // Simplified external pressure coefficient for windward face

// 1. Convert wind speed from kph to m/s
const design_wind_speed_m_s = design_wind_speed_kph * 1000 / 3600; // m/s

// 2. Determine Exposure Coefficient (Kz) based on terrain and height
let exposure_coefficient_Kz;
if (terrain_category === 'open') {
    if (building_height_m <= 10) exposure_coefficient_Kz = 0.85;
    else if (building_height_m <= 20) exposure_coefficient_Kz = 0.95;
    else if (building_height_m <= 40) exposure_coefficient_Kz = 1.05;
    else exposure_coefficient_Kz = 1.15;
} else if (terrain_category === 'suburban') {
    if (building_height_m <= 10) exposure_coefficient_Kz = 0.60;
    else if (building_height_m <= 20) exposure_coefficient_Kz = 0.70;
    else if (building_height_m <= 40) exposure_coefficient_Kz = 0.80;
    else exposure_coefficient_Kz = 0.90;
} else if (terrain_category === 'urban') {
    if (building_height_m <= 10) exposure_coefficient_Kz = 0.45;
    else if (building_height_m <= 20) exposure_coefficient_Kz = 0.55;
    else if (building_height_m <= 40) exposure_coefficient_Kz = 0.65;
    else exposure_coefficient_Kz = 0.75;
} else {
    // Default to suburban if unknown, or throw error
    exposure_coefficient_Kz = 0.70; // A reasonable mid-range default
    console.warn("Unknown terrain category, defaulting Kz to Suburban (10-20m).");
}

// 3. Determine Importance Factor (I) based on occupancy category
let importance_factor_I;
switch (building_occupancy_category) {
    case 'I':
        importance_factor_I = 0.87; // Low hazard to human life
        break;
    case 'II':
        importance_factor_I = 1.00; // Standard occupancy buildings
        break;
    case 'III':
    case 'IV': // Essential facilities and substantial hazard (often grouped for wind)
        importance_factor_I = 1.15;
        break;
    default:
        importance_factor_I = 1.00; // Default to standard if unknown
        console.warn("Unknown occupancy category, defaulting I to Category II.");
}

// 4. Calculate Design Wind Pressure (P)
// Formula: P = 0.5 * rho * V^2 * Kz * G * Cp * I
// Result in Pascals (N/m^2)
const design_wind_pressure_pa = 0.5 * air_density_kg_m3 * Math.pow(design_wind_speed_m_s, 2) * exposure_coefficient_Kz * gust_effect_factor * pressure_coefficient * importance_factor_I;

// Convert to kPa
const design_wind_pressure_kpa = design_wind_pressure_pa / 1000;

// 5. Calculate Total Wind Load (F)
// Projected area = Building Height * Building Width
const projected_area_sqm = building_height_m * building_width_m;
// Total Load in Newtons (N)
const total_wind_load_newtons = design_wind_pressure_pa * projected_area_sqm;

// Convert to kilonewtons (kN)
const total_wind_load_kn = total_wind_load_newtons / 1000;

// 6. Estimate Damage Risk
// Simplified model: Risk is proportional to wind load and inversely proportional to resilience.
// A reference 'resilience threshold' is needed, let's assume a baseline capacity.
// Example: A typical non-resilient building might start seeing damage at 50 kN/m^2 effective load (arbitrary for calculation).
// Or, more simply, relative to some baseline load capacity scaled by resilience factor.
const baseline_capacity_factor = 200; // Arbitrary baseline load capacity in kN for a highly resilient building (structural_resilience_factor = 5)
const effective_capacity = baseline_capacity_factor * (structural_resilience_factor / 5); // Scale capacity by resilience (5 is max)

let damage_risk_percentage_raw = (total_wind_load_kn / effective_capacity) * 100;

// Cap risk between 0 and 100%
const damage_risk_percentage = Math.min(Math.max(damage_risk_percentage_raw, 0), 100);

return {
    design_wind_pressure_kpa,
    total_wind_load_kn,
    damage_risk_percentage
};
