function calculate({ fixedCosts, sellingPricePerUnit, variableCostPerUnit }) {
  if (fixedCosts <= 0 || sellingPricePerUnit <= 0 || variableCostPerUnit < 0) {
    return { error: 'All inputs must be positive numbers. Fixed costs and selling price must be greater than zero.' };
  }

  const contributionMarginPerUnit = sellingPricePerUnit - variableCostPerUnit;

  if (contributionMarginPerUnit <= 0) {
    return { error: 'Selling price per unit must be greater than variable cost per unit to achieve a positive contribution margin.' };
  }

  const breakEvenUnits = fixedCosts / contributionMarginPerUnit;
  const breakEvenRevenue = breakEvenUnits * sellingPricePerUnit;

  return {
    breakEvenUnits: Math.ceil(breakEvenUnits), // Rounded up as you cannot sell a fraction of a unit to cover costs
    breakEvenRevenue: breakEvenRevenue
  };
}
