const { fixedCosts, sellingPricePerUnit, variableCostPerUnit } = inputs;
const fc = parseFloat(fixedCosts) || 0;
const price = parseFloat(sellingPricePerUnit) || 0;
const vc = parseFloat(variableCostPerUnit) || 0;

if (price <= vc) return { breakEvenUnits: 0, breakEvenRevenue: 0, contributionMargin: 0 };

const contributionMargin = price - vc;
const units = fc / contributionMargin;
const revenue = units * price;

return {
  breakEvenUnits: Math.ceil(units),
  breakEvenRevenue: revenue,
  contributionMargin
};
