function calculate({ annualRevenue, ebitda, netIncome, revenueMultiple, ebitdaMultiple, netIncomeMultiple }) {
    const ar = parseFloat(annualRevenue) || 0;
    const e = parseFloat(ebitda) || 0;
    const ni = parseFloat(netIncome) || 0;
    const rm = parseFloat(revenueMultiple) || 0;
    const em = parseFloat(ebitdaMultiple) || 0;
    const nim = parseFloat(netIncomeMultiple) || 0;

    let estimatedRevenueValuation = 0;
    if (ar > 0 && rm > 0) {
        estimatedRevenueValuation = ar * rm;
    }

    let estimatedEBITDAValuation = 0;
    if (e > 0 && em > 0) {
        estimatedEBITDAValuation = e * em;
    }

    let estimatedNetIncomeValuation = 0;
    if (ni > 0 && nim > 0) {
        estimatedNetIncomeValuation = ni * nim;
    }

    let validValuations = [];
    if (estimatedRevenueValuation > 0) {
        validValuations.push(estimatedRevenueValuation);
    }
    if (estimatedEBITDAValuation > 0) {
        validValuations.push(estimatedEBITDAValuation);
    }
    if (estimatedNetIncomeValuation > 0) {
        validValuations.push(estimatedNetIncomeValuation);
    }

    let averageValuation = 0;
    if (validValuations.length > 0) {
        const sum = validValuations.reduce((acc, val) => acc + val, 0);
        averageValuation = sum / validValuations.length;
    }

    let finalAverageValuation = averageValuation;

    // Example of conditional logic and a cap for complexity:
    // If Net Income is disproportionately low compared to EBITDA, suggesting high non-operating expenses or taxes,
    // we might apply a weighted average or a cap to the overall average valuation.
    // Here, if Net Income is less than 10% of EBITDA (and EBITDA is positive), we apply a re-weighting.
    if (e > 0 && ni / e < 0.1 && validValuations.length > 1) {
        const ebitdaWeight = 0.5;
        const revenueWeight = 0.3;
        const netIncomeWeight = 0.2; // Reduced weight for NI due to low margin

        let weightedSum = 0;
        let totalWeight = 0;

        if (estimatedRevenueValuation > 0) {
            weightedSum += estimatedRevenueValuation * revenueWeight;
            totalWeight += revenueWeight;
        }
        if (estimatedEBITDAValuation > 0) {
            weightedSum += estimatedEBITDAValuation * ebitdaWeight;
            totalWeight += ebitdaWeight;
        }
        if (estimatedNetIncomeValuation > 0) {
            weightedSum += estimatedNetIncomeValuation * netIncomeWeight;
            totalWeight += netIncomeWeight;
        }

        finalAverageValuation = totalWeight > 0 ? weightedSum / totalWeight : 0;

        // Further cap: ensure the re-weighted average doesn't exceed 120% of the highest individual valuation
        const maxIndividualVal = Math.max(...validValuations, 0);
        if (maxIndividualVal > 0) {
            finalAverageValuation = Math.min(finalAverageValuation, maxIndividualVal * 1.2);
        }
    }

    return {
        estimatedRevenueValuation,
        estimatedEBITDAValuation,
        estimatedNetIncomeValuation,
        averageValuation: finalAverageValuation,
    };
}
