const initial = parseFloat(inputs.initialInvestment);
const final = parseFloat(inputs.finalInvestment);
const years = parseFloat(inputs.numberOfYears);

let cagr = NaN;
let totalGrowth = NaN;
let totalGrowthPercentage = NaN;

if (isNaN(initial) || isNaN(final) || isNaN(years)) {
    return { cagr, totalGrowth, totalGrowthPercentage };
}

// Handle edge cases for years
if (years <= 0) {
    totalGrowth = final - initial;
    if (initial !== 0) {
        totalGrowthPercentage = (totalGrowth / initial) * 100;
    } else { // initial === 0
        totalGrowthPercentage = (final > 0) ? Infinity : (final < 0 ? -Infinity : 0);
    }
    return { cagr, totalGrowth, totalGrowthPercentage };
}

// Handle initial investment of zero
if (initial === 0) {
    totalGrowth = final - initial;
    totalGrowthPercentage = (final > 0) ? Infinity : (final < 0 ? -Infinity : 0);
    return { cagr, totalGrowth, totalGrowthPercentage };
}

// Handle negative initial investment (CAGR often not well-defined or interpretable)
if (initial < 0) {
    // For practical purposes in most financial contexts, a negative initial investment
    // makes CAGR difficult to interpret meaningfully. Returning NaN is often appropriate.
    // However, total growth calculations are still possible.
    totalGrowth = final - initial;
    totalGrowthPercentage = (totalGrowth / initial) * 100;
    return { cagr, totalGrowth, totalGrowthPercentage };
}

// Standard CAGR calculation
cagr = (Math.pow(final / initial, 1 / years) - 1) * 100;

// Total growth calculations
totalGrowth = final - initial;
totalGrowthPercentage = (totalGrowth / initial) * 100;

return { cagr, totalGrowth, totalGrowthPercentage };
