formula:
const { currentWeight, targetWeight, timeframeWeeks, gender, age, heightCm, activityLevel } = inputs;

// Map select options to multipliers
let actMult = 1.2;
if (activityLevel.includes('Lightly')) actMult = 1.375;
else if (activityLevel.includes('Moderately')) actMult = 1.55;
else if (activityLevel.includes('Very')) actMult = 1.725;
else if (activityLevel.includes('Extra')) actMult = 1.9;

const POUNDS_TO_KG = 0.453592;
const CALORIES_PER_KG_FAT = 7700;

const weightKg = currentWeight * POUNDS_TO_KG;
const targetKg = targetWeight * POUNDS_TO_KG;
const lossKg = weightKg - targetKg;

if (lossKg <= 0) return { dailyCalorieDeficit: 0, tdee: 0, targetDailyCalories: 0 };

// Mifflin-St Jeor
let bmr = (10 * weightKg) + (6.25 * heightCm) - (5 * age);
if (gender === 'Male') bmr += 5;
else bmr -= 161;

const tdee = bmr * actMult;
const totalDeficitNeeded = lossKg * CALORIES_PER_KG_FAT;
const days = timeframeWeeks * 7;
const dailyDeficit = totalDeficitNeeded / days;
const targetCalories = tdee - dailyDeficit;

// Safety check (1200 female / 1500 male)
const minCal = (gender === 'Male') ? 1500 : 1200;

return {
  dailyCalorieDeficit: dailyDeficit,
  tdee: tdee,
  targetDailyCalories: Math.max(targetCalories, minCal)
};
