(inputs) => {
    const { currentWeight, targetWeight, timeframeWeeks, gender, age, heightCm, activityLevel } = inputs;

    // --- Input Validations ---
    if (currentWeight <= 0 || targetWeight <= 0 || age <= 0 || heightCm <= 0 || timeframeWeeks <= 0) {
        throw new Error("All numerical inputs must be positive.");
    }
    if (targetWeight >= currentWeight) {
        throw new Error("Target weight must be less than current weight.");
    }
    const lowerCaseGender = gender.toLowerCase();
    if (!['male', 'female'].includes(lowerCaseGender)) {
        throw new Error("Gender must be 'male' or 'female'.");
    }
    if (activityLevel < 1.2 || activityLevel > 1.9) {
         throw new Error("Activity level must be between 1.2 (sedentary) and 1.9 (extra active).");
    }

    // --- Constants ---
    const POUNDS_TO_KG = 0.453592;
    const CALORIES_PER_KG_FAT = 7700; // Approximate calories in 1 kg of body fat

    // --- Calculations ---
    const currentWeightKg = currentWeight * POUNDS_TO_KG;
    const targetWeightKg = targetWeight * POUNDS_TO_KG;
    const weightLossKg = currentWeightKg - targetWeightKg;

    // Mifflin-St Jeor Equation for BMR (using current weight)
    let bmr;
    if (lowerCaseGender === 'male') {
        bmr = (10 * currentWeightKg) + (6.25 * heightCm) - (5 * age) + 5;
    } else { // female
        bmr = (10 * currentWeightKg) + (6.25 * heightCm) - (5 * age) - 161;
    }

    // Calculate TDEE (Total Daily Energy Expenditure)
    // Ensure TDEE is at least BMR * 1.2 (sedentary minimum)
    let tdee = bmr * activityLevel;
    tdee = Math.max(tdee, bmr * 1.2); // Ensure TDEE is not less than sedentary BMR

    // Total calories to burn for the target weight loss
    const totalCaloriesToBurn = weightLossKg * CALORIES_PER_KG_FAT;

    // Total days in the timeframe
    const totalDays = timeframeWeeks * 7;

    // The deficit *required* to hit the user's specific target
    const calculatedDailyDeficit = totalCaloriesToBurn / totalDays;

    // The daily calorie intake based on the calculated deficit
    const calculatedTargetDailyCalories = tdee - calculatedDailyDeficit;

    // --- Safety & Recommendation Caps ---
    const minimumHealthyIntake = lowerCaseGender === 'male' ? 1500 : 1200; // General safe minimum intake
    const maxRecommendedDailyDeficit = 1000; // Max generally recommended deficit for sustainable loss (approx 2 lbs/week)

    let finalDailyCalorieDeficit;
    let finalTargetDailyCalories;

    // Scenario 1: Calculated intake is too low. Cap intake at minimum, derive deficit.
    if (calculatedTargetDailyCalories < minimumHealthyIntake) {
        finalTargetDailyCalories = minimumHealthyIntake;
        finalDailyCalorieDeficit = tdee - minimumHealthyIntake;
    } else {
        // Scenario 2: Calculated intake is safe. Use calculated values.
        finalTargetDailyCalories = calculatedTargetDailyCalories;
        finalDailyCalorieDeficit = calculatedDailyDeficit;
    }

    // Scenario 3: The derived deficit (from either above scenario) is too high (exceeds max recommended).
    // Cap deficit at max recommended, then derive target intake.
    if (finalDailyCalorieDeficit > maxRecommendedDailyDeficit) {
        finalDailyCalorieDeficit = maxRecommendedDailyDeficit;
        finalTargetDailyCalories = tdee - maxRecommendedDailyDeficit;
    }

    // Final safety check: ensure target calories are not below minimumHealthyIntake
    // (could happen if TDEE itself is extremely low, leading to a negative deficit when capped)
    finalTargetDailyCalories = Math.max(finalTargetDailyCalories, minimumHealthyIntake);
    // And recalculate deficit based on this last cap
    finalDailyCalorieDeficit = tdee - finalTargetDailyCalories;

    // Ensure deficit is not negative (meaning intake > TDEE, which implies weight gain, not loss)
    finalDailyCalorieDeficit = Math.max(0, finalDailyCalorieDeficit);

    return {
        dailyCalorieDeficit: Math.round(finalDailyCalorieDeficit),
        tdee: Math.round(tdee),
        targetDailyCalories: Math.round(finalTargetDailyCalories)
    };
}
