const { buildingPopulation, numberOfExits, avgExitWidthFt, avgEgressTravelDistanceFt, assemblyPointCapacityFactorSqFtPerPerson, egressFlowRatePersonPerMinPerFoot } = inputs;

// Egress Time Calculation
const totalExitWidthFt = numberOfExits * avgExitWidthFt;
const effectiveEgressFlowRatePersonsPerMin = totalExitWidthFt * egressFlowRatePersonPerMinPerFoot;

let estimatedEgressTimeMinutes;
if (effectiveEgressFlowRatePersonsPerMin <= 0 || buildingPopulation <= 0) {
    estimatedEgressTimeMinutes = 999; // Indicate very poor or impossible egress
} else {
    estimatedEgressTimeMinutes = buildingPopulation / effectiveEgressFlowRatePersonsPerMin;
    // Add a safety factor (e.g., 20% for potential bottlenecks, human behavior)
    estimatedEgressTimeMinutes *= 1.2;
    // Ensure a minimum egress time even for small populations
    estimatedEgressTimeMinutes = Math.max(2, estimatedEgressTimeMinutes);
}

// Recommended Assembly Point Area
const recommendedAssemblyPointAreaSqFt = buildingPopulation * assemblyPointCapacityFactorSqFtPerPerson;

// Egress Path Efficiency Rating (0-100%)
// This metric considers the balance between population, exit capacity, and travel distance.
// A lower egress time and shorter travel distance with adequate exits lead to higher efficiency.
// Ideal scenarios: Egress time < 5 min, Travel distance < 150 ft.
const idealEgressTimeThreshold = 5; // minutes
const idealTravelDistanceThreshold = 150; // feet

let timeEfficiency = 0;
if (estimatedEgressTimeMinutes < idealEgressTimeThreshold) {
    timeEfficiency = 1; // Excellent
} else if (estimatedEgressTimeMinutes < idealEgressTimeThreshold * 2) { // up to 10 minutes
    timeEfficiency = (idealEgressTimeThreshold * 2 - estimatedEgressTimeMinutes) / idealEgressTimeThreshold;
} else {
    timeEfficiency = 0; // Very poor
}
timeEfficiency = Math.max(0, Math.min(1, timeEfficiency)); // Cap between 0 and 1

let travelDistanceEfficiency = 0;
if (avgEgressTravelDistanceFt < idealTravelDistanceThreshold) {
    travelDistanceEfficiency = 1; // Excellent
} else if (avgEgressTravelDistanceFt < idealTravelDistanceThreshold * 2) { // up to 300 feet
    travelDistanceEfficiency = (idealTravelDistanceThreshold * 2 - avgEgressTravelDistanceFt) / idealTravelDistanceThreshold;
} else {
    travelDistanceEfficiency = 0; // Very poor
}
travelDistanceEfficiency = Math.max(0, Math.min(1, travelDistanceEfficiency)); // Cap between 0 and 1

// Combine efficiencies, giving more weight to egress time (e.g., 60% time, 40% distance)
let egressPathEfficiencyRating = (timeEfficiency * 0.6 + travelDistanceEfficiency * 0.4) * 100;
egressPathEfficiencyRating = Math.max(0, Math.min(100, egressPathEfficiencyRating)); // Cap between 0 and 100

return {
    estimatedEgressTimeMinutes,
    recommendedAssemblyPointAreaSqFt,
    egressPathEfficiencyRating
};
