const longTermBrackets = {
    "Single": [
        { rate: 0.00, limit: 44625 },
        { rate: 0.15, limit: 492300 },
        { rate: 0.20, limit: Infinity }
    ],
    "Married Filing Jointly": [
        { rate: 0.00, limit: 89250 },
        { rate: 0.15, limit: 553850 },
        { rate: 0.20, limit: Infinity }
    ],
    "Head of Household": [
        { rate: 0.00, limit: 59750 },
        { rate: 0.15, limit: 523050 },
        { rate: 0.20, limit: Infinity }
    ]
};

const ordinaryBrackets = {
    "Single": [
        { rate: 0.10, limit: 11600 },
        { rate: 0.12, limit: 47150 },
        { rate: 0.22, limit: 100525 },
        { rate: 0.24, limit: 191950 },
        { rate: 0.32, limit: 243725 },
        { rate: 0.35, limit: 609350 },
        { rate: 0.37, limit: Infinity }
    ],
    "Married Filing Jointly": [
        { rate: 0.10, limit: 23200 },
        { rate: 0.12, limit: 94300 },
        { rate: 0.22, limit: 201050 },
        { rate: 0.24, limit: 383900 },
        { rate: 0.32, limit: 487450 },
        { rate: 0.35, limit: 731200 },
        { rate: 0.37, limit: Infinity }
    ],
    "Head of Household": [
        { rate: 0.10, limit: 16550 },
        { rate: 0.12, limit: 63100 },
        { rate: 0.22, limit: 100500 },
        { rate: 0.24, limit: 191950 },
        { rate: 0.32, limit: 243700 },
        { rate: 0.35, limit: 609350 },
        { rate: 0.37, limit: Infinity }
    ]
};

const capitalGain = Math.max(0, assetSalePrice - assetCostBasis - sellingExpenses);
let federalTax = 0;

if (capitalGain > 0) {
    const isShortTerm = holdingPeriodMonths <= 12;
    let normalizedFilingStatus = 'Single'; // Default

    if (filingStatus.toLowerCase().includes('married filing jointly')) {
        normalizedFilingStatus = 'Married Filing Jointly';
    } else if (filingStatus.toLowerCase().includes('head of household')) {
        normalizedFilingStatus = 'Head of Household';
    } else if (filingStatus.toLowerCase().includes('single')) {
        normalizedFilingStatus = 'Single';
    }

    let bracketsToUse;
    if (isShortTerm) {
        bracketsToUse = ordinaryBrackets[normalizedFilingStatus];
    } else {
        bracketsToUse = longTermBrackets[normalizedFilingStatus];
    }

    if (!bracketsToUse) {
        throw new Error("Unsupported filing status. Please use 'Single', 'Married Filing Jointly', or 'Head of Household'.");
    }

    let incomeRemaining = otherTaxableIncome + capitalGain;
    let taxableGainPortion = capitalGain; // Track how much of the gain is left to be taxed

    for (let i = bracketsToUse.length - 1; i >= 0; i--) {
        const bracket = bracketsToUse[i];
        const prevLimit = (i === 0) ? 0 : bracketsToUse[i-1].limit;

        // The amount of income that falls within *this* bracket's range
        const incomeInBracketRange = Math.max(0, Math.min(incomeRemaining, bracket.limit) - prevLimit);

        // The amount of otherTaxableIncome that falls within this bracket's range
        const otherIncomeInBracketRange = Math.max(0, Math.min(otherTaxableIncome, bracket.limit) - prevLimit);

        // The amount of capital gain that falls within this bracket
        const gainInBracket = incomeInBracketRange - otherIncomeInBracketRange;

        if (gainInBracket > 0) {
            federalTax += Math.min(gainInBracket, taxableGainPortion) * bracket.rate;
            taxableGainPortion -= Math.min(gainInBracket, taxableGainPortion);
        }

        incomeRemaining = Math.min(incomeRemaining, prevLimit); // Move to the next lower bracket
    }
}

const stateTaxRate = stateCapitalGainsTaxRate || 0;
const stateTax = capitalGain * (stateTaxRate / 100);
const netProfit = capitalGain - federalTax - stateTax;

return {
    totalCapitalGain: capitalGain,
    federalTaxEstimate: federalTax,
    stateTaxEstimate: stateTax,
    netProfitAfterTax: netProfit
};
