function calculate() {
    const { assetSalePrice, assetPurchasePrice, brokerageFees, holdingMonths, taxableIncome, filingStatus, priorYearCapitalLosses } = inputs;

    const grossGain = assetSalePrice - assetPurchasePrice - brokerageFees;

    let shortTermGainAmount = 0;
    let longTermGainAmount = 0;
    let netCapitalLoss = 0;

    if (grossGain > 0) {
        if (holdingMonths < 12) {
            shortTermGainAmount = grossGain;
        } else {
            longTermGainAmount = grossGain;
        }
    } else {
        netCapitalLoss = Math.abs(grossGain);
    }

    // Apply prior year capital losses (simplified: offsets gains directly, ST first then LT)
    let currentShortTermTaxable = shortTermGainAmount;
    let currentLongTermTaxable = longTermGainAmount;
    let availableLosses = priorYearCapitalLosses;

    if (availableLosses > 0) {
        // Offset short-term gains first
        const offsetST = Math.min(availableLosses, currentShortTermTaxable);
        currentShortTermTaxable -= offsetST;
        availableLosses -= offsetST;

        // Then offset long-term gains
        const offsetLT = Math.min(availableLosses, currentLongTermTaxable);
        currentLongTermTaxable -= offsetLT;
        availableLosses -= offsetLT;
    }

    let ordinaryTax = 0;
    let longTermTax = 0;

    // Tax Bracket Definitions (Simplified examples for 2023/2024 tax year)
    const ordinaryTaxBrackets = {
        'single': [
            { limit: 11600, rate: 0.10 },
            { limit: 47150, rate: 0.12 },
            { limit: 100525, rate: 0.22 },
            { limit: 191950, rate: 0.24 },
            { limit: 243725, rate: 0.32 },
            { limit: 609350, rate: 0.35 },
            { limit: Infinity, rate: 0.37 }
        ],
        'married-filing-jointly': [
            { limit: 23200, rate: 0.10 },
            { limit: 94300, rate: 0.12 },
            { limit: 201050, rate: 0.22 },
            { limit: 383900, rate: 0.24 },
            { limit: 487450, rate: 0.32 },
            { limit: 731200, rate: 0.35 },
            { limit: Infinity, rate: 0.37 }
        ]
    };

    const longTermCapitalGainsBrackets = {
        'single': [
            { incomeLimit: 47025, rate: 0.00 },
            { incomeLimit: 518900, rate: 0.15 },
            { incomeLimit: Infinity, rate: 0.20 }
        ],
        'married-filing-jointly': [
            { incomeLimit: 94050, rate: 0.00 },
            { incomeLimit: 583750, rate: 0.15 },
            { incomeLimit: Infinity, rate: 0.20 }
        ]
    };

    // Default to 'single' if filing status is not recognized
    const selectedOrdinaryBrackets = ordinaryTaxBrackets[filingStatus] || ordinaryTaxBrackets['single'];
    const selectedLongTermBrackets = longTermCapitalGainsBrackets[filingStatus] || longTermCapitalGainsBrackets['single'];

    if (grossGain > 0) {
        // Calculate Ordinary Income Tax (includes short-term gains)
        let currentOrdinaryTaxableIncome = taxableIncome + currentShortTermTaxable;
        let prevOrdinaryLimit = 0;
        let tempTaxableIncomeForOrdinaryCalc = currentOrdinaryTaxableIncome;

        for (const bracket of selectedOrdinaryBrackets) {
            if (tempTaxableIncomeForOrdinaryCalc <= prevOrdinaryLimit) break;
            const taxableInThisBracket = Math.min(tempTaxableIncomeForOrdinaryCalc, bracket.limit) - prevOrdinaryLimit;
            ordinaryTax += taxableInThisBracket * bracket.rate;
            prevOrdinaryLimit = bracket.limit;
        }

        // Calculate Long-Term Capital Gains Tax
        let incomeForLTGCBrackets = taxableIncome + currentShortTermTaxable; // Base income for determining LTGC bracket rates
        let remainingLTGainToTax = currentLongTermTaxable;

        for (const bracket of selectedLongTermBrackets) {
            if (remainingLTGainToTax <= 0) break;

            const bracketThreshold = bracket.incomeLimit;
            let taxableAtCurrentRate = 0;

            if (incomeForLTGCBrackets < bracketThreshold) {
                const spaceInBracket = bracketThreshold - incomeForLTGCBrackets;
                taxableAtCurrentRate = Math.min(remainingLTGainToTax, spaceInBracket);
                longTermTax += taxableAtCurrentRate * bracket.rate;
                incomeForLTGCBrackets += taxableAtCurrentRate; // Update cumulative income
                remainingLTGainToTax -= taxableAtCurrentRate;
            } else {
                taxableAtCurrentRate = remainingLTGainToTax;
                longTermTax += taxableAtCurrentRate * bracket.rate;
                incomeForLTGCBrackets += taxableAtCurrentRate; // Update cumulative income
                remainingLTGainToTax -= taxableAtCurrentRate;
            }
        }
    }

    const totalCapitalGainResult = grossGain;
    const estimatedTaxLiabilityResult = ordinaryTax + longTermTax;
    const netProfitAfterTaxResult = grossGain - estimatedTaxLiabilityResult;

    return {
        totalCapitalGain: totalCapitalGainResult,
        estimatedTaxLiability: Math.max(0, estimatedTaxLiabilityResult), // Tax liability cannot be negative
        netProfitAfterTax: netProfitAfterTaxResult
    };
}
