function calculate({ riskFreeRate, beta, expectedMarketReturn }) {
  // Convert percentage inputs to decimals for calculation
  const rf = riskFreeRate / 100;
  const rm = expectedMarketReturn / 100;

  // Calculate Market Risk Premium
  const marketRiskPremium = rm - rf;

  // Calculate Required Rate of Return using CAPM formula
  const requiredReturn = rf + (beta * marketRiskPremium);

  // Convert results back to percentage for output
  return {
    requiredReturn: requiredReturn * 100,
    marketRiskPremium: marketRiskPremium * 100,
  };
}
