function calculateROI(inputs) {
    const {
        initialInvestment,
        projectLifespanYears,
        annualOperationalCost,
        carbonSequestrationRate,
        currentCarbonCreditPrice,
        carbonCreditPriceAnnualGrowthRate,
        annualOperationalCostInflationRate,
        discountRate
    } = inputs;

    // Input validations
    if (projectLifespanYears <= 0) {
        return {
            totalInitialInvestment: initialInvestment,
            totalProjectCost: initialInvestment,
            totalCarbonSequestered: 0,
            totalCarbonCreditRevenue: 0,
            netPresentValue: -initialInvestment,
            returnOnInvestment: initialInvestment === 0 ? 0 : -100,
            paybackPeriodYears: null
        };
    }

    const discountFactor = 1 + (discountRate / 100);
    const costInflationFactor = 1 + (annualOperationalCostInflationRate / 100);
    const creditPriceGrowthFactor = 1 + (carbonCreditPriceAnnualGrowthRate / 100);

    let totalDiscountedOperationalCosts = 0;
    let totalDiscountedCarbonCreditRevenue = 0;

    let cumulativeUndiscountedCashFlow = -initialInvestment;
    let paybackPeriodYears = null;

    for (let i = 1; i <= projectLifespanYears; i++) {
        const currentYearOperationalCost = annualOperationalCost * Math.pow(costInflationFactor, i - 1);
        const currentYearCarbonCreditPrice = currentCarbonCreditPrice * Math.pow(creditPriceGrowthFactor, i - 1);
        const currentYearCarbonCreditRevenue = carbonSequestrationRate * currentYearCarbonCreditPrice;

        const discountedOperationalCost = currentYearOperationalCost / Math.pow(discountFactor, i);
        const discountedCarbonCreditRevenue = currentYearCarbonCreditRevenue / Math.pow(discountFactor, i);

        totalDiscountedOperationalCosts += discountedOperationalCost;
        totalDiscountedCarbonCreditRevenue += discountedCarbonCreditRevenue;

        // Calculate simple payback period
        if (paybackPeriodYears === null) {
            cumulativeUndiscountedCashFlow += (currentYearCarbonCreditRevenue - currentYearOperationalCost);
            if (cumulativeUndiscountedCashFlow >= 0) {
                // Linear interpolation for more precise payback period within the year
                const previousYearCumulative = cumulativeUndiscountedCashFlow - (currentYearCarbonCreditRevenue - currentYearOperationalCost);
                const cashFlowThisYear = currentYearCarbonCreditRevenue - currentYearOperationalCost;
                if (cashFlowThisYear > 0) { 
                    paybackPeriodYears = (i - 1) + (Math.abs(previousYearCumulative) / cashFlowThisYear);
                } else if (cumulativeUndiscountedCashFlow >= 0) { 
                    paybackPeriodYears = i;
                }
            }
        }
    }

    const totalCarbonSequestered = carbonSequestrationRate * projectLifespanYears;
    const totalProjectCost = initialInvestment + totalDiscountedOperationalCosts;
    const netPresentValue = totalDiscountedCarbonCreditRevenue - totalProjectCost;

    let returnOnInvestment = 0;
    if (totalProjectCost > 0) {
        returnOnInvestment = (netPresentValue / totalProjectCost) * 100;
    } else if (netPresentValue > 0) { 
        returnOnInvestment = Infinity; 
    } else { 
        returnOnInvestment = 0;
    }

    // Handle edge case for payback period if initialInvestment is 0
    if (initialInvestment === 0) {
        paybackPeriodYears = 0; 
    } else if (paybackPeriodYears === null && cumulativeUndiscountedCashFlow < 0) {
        paybackPeriodYears = null; 
    }

    return {
        totalInitialInvestment: initialInvestment,
        totalProjectCost: totalProjectCost,
        totalCarbonSequestered: totalCarbonSequestered,
        totalCarbonCreditRevenue: totalDiscountedCarbonCreditRevenue,
        netPresentValue: netPresentValue,
        returnOnInvestment: returnOnInvestment,
        paybackPeriodYears: paybackPeriodYears
    };
}
