function calculateChildCareCosts(inputs) {
    const annual_household_income = parseFloat(inputs.annual_household_income);
    const state = inputs.state.toUpperCase();
    const num_children_0_2 = parseInt(inputs.num_children_0_2);
    const num_children_3_5 = parseInt(inputs.num_children_3_5);
    const num_children_6_12 = parseInt(inputs.num_children_6_12);
    const care_type = inputs.care_type.toLowerCase(); // 'center' or 'home-based'
    const is_full_time_care = inputs.is_full_time_care.toLowerCase() === 'yes';

    let estimated_annual_cost = 0;
    let potential_subsidy_amount = 0;

    const total_children_in_care = num_children_0_2 + num_children_3_5 + num_children_6_12;

    if (total_children_in_care === 0) {
        return {
            estimated_annual_cost: 0,
            potential_subsidy_amount: 0,
            net_annual_cost_after_subsidy: 0,
            cost_as_percent_of_income: 0
        };
    }

    // Simplified State Data Lookup (Illustrative values)
    const STATE_DATA = {
        "NY": {
            avg_cost_infant_center: 21000, avg_cost_toddler_center: 18000, avg_cost_preschool_center: 16000, avg_cost_school_age_center: 10000,
            avg_cost_infant_home: 18000, avg_cost_toddler_home: 15000, avg_cost_preschool_home: 13000, avg_cost_school_age_home: 8000,
            smi_threshold_percent: 0.85, smi_2person: 85000, smi_3person: 98000, smi_4person: 110000, smi_5person: 120000,
            subsidy_coverage_percent: 0.70, subsidy_cap_per_child: 12000,
            copay_rate_percent: 0.05, co_pay_income_threshold_fpl_multiplier: 1.0,
            fpl_2person: 19720, fpl_3person: 24860, fpl_4person: 30000, fpl_5person: 35140
        },
        "TX": {
            avg_cost_infant_center: 10000, avg_cost_toddler_center: 9000, avg_cost_preschool_center: 8000, avg_cost_school_age_center: 6000,
            avg_cost_infant_home: 9000, avg_cost_toddler_home: 8000, avg_cost_preschool_home: 7000, avg_cost_school_age_home: 5000,
            smi_threshold_percent: 0.80, smi_2person: 70000, smi_3person: 80000, smi_4person: 90000, smi_5person: 100000,
            subsidy_coverage_percent: 0.60, subsidy_cap_per_child: 8000,
            copay_rate_percent: 0.03, co_pay_income_threshold_fpl_multiplier: 1.0,
            fpl_2person: 19720, fpl_3person: 24860, fpl_4person: 30000, fpl_5person: 35140
        },
        "IL": {
            avg_cost_infant_center: 15000, avg_cost_toddler_center: 13000, avg_cost_preschool_center: 11000, avg_cost_school_age_center: 7500,
            avg_cost_infant_home: 13000, avg_cost_toddler_home: 11000, avg_cost_preschool_home: 9500, avg_cost_school_age_home: 6000,
            smi_threshold_percent: 0.85, smi_2person: 78000, smi_3person: 90000, smi_4person: 102000, smi_5person: 112000,
            subsidy_coverage_percent: 0.65, subsidy_cap_per_child: 10000,
            copay_rate_percent: 0.04, co_pay_income_threshold_fpl_multiplier: 1.0,
            fpl_2person: 19720, fpl_3person: 24860, fpl_4person: 30000, fpl_5person: 35140
        },
        "DEFAULT": {
            avg_cost_infant_center: 14000, avg_cost_toddler_center: 12000, avg_cost_preschool_center: 10000, avg_cost_school_age_center: 7000,
            avg_cost_infant_home: 12000, avg_cost_toddler_home: 10000, avg_cost_preschool_home: 8500, avg_cost_school_age_home: 5500,
            smi_threshold_percent: 0.80, smi_2person: 75000, smi_3person: 88000, smi_4person: 99000, smi_5person: 109000,
            subsidy_coverage_percent: 0.65, subsidy_cap_per_child: 9000,
            copay_rate_percent: 0.04, co_pay_income_threshold_fpl_multiplier: 1.0,
            fpl_2person: 19720, fpl_3person: 24860, fpl_4person: 30000, fpl_5person: 35140
        }
    };

    const state_data = STATE_DATA[state] || STATE_DATA["DEFAULT"];

    // Helper to get FPL/SMI based on family size
    const getFamilyLevel = (data, type, familySize) => {
        let fSize = Math.min(familySize, 5); // Capping at 5 for direct lookup, then extrapolate
        if (familySize <= 2) return data[`${type}_2person`];
        if (familySize === 3) return data[`${type}_3person`];
        if (familySize === 4) return data[`${type}_4person`];
        // For 5 or more, use 5-person base and add for additional members (simplified linear increase)
        if (familySize >= 5) return data[`${type}_5person`] + (familySize - 5) * 5000; 
        return 0; 
    };

    // 1. Estimate Base Child Care Cost
    const care_type_prefix = care_type === 'home-based' ? 'avg_cost_' : 'avg_cost_'; // Both use 'avg_cost_' for consistency, distinction is in the age keys
    const care_type_suffix = care_type === 'home-based' ? '_home' : '_center';
    const part_time_multiplier = is_full_time_care ? 1.0 : 0.6;

    for (let i = 0; i < num_children_0_2; i++) {
        estimated_annual_cost += (state_data[`avg_cost_infant` + care_type_suffix] || state_data[`avg_cost_toddler` + care_type_suffix]) * part_time_multiplier; // Use infant/toddler cost for 0-2
    }
    for (let i = 0; i < num_children_3_5; i++) {
        estimated_annual_cost += state_data[`avg_cost_preschool` + care_type_suffix] * part_time_multiplier;
    }
    for (let i = 0; i < num_children_6_12; i++) {
        estimated_annual_cost += state_data[`avg_cost_school_age` + care_type_suffix] * part_time_multiplier;
    }

    // 2. Determine Subsidy Eligibility and Amount
    let family_size = total_children_in_care + 2; // Assuming 2 adults + children for household size
    if (family_size < 2) family_size = 2; // Minimum family size for FPL/SMI

    const state_median_income = getFamilyLevel(state_data, 'smi', family_size);
    const fpl_income = getFamilyLevel(state_data, 'fpl', family_size);

    const smi_eligibility_threshold = state_median_income * state_data.smi_threshold_percent;

    if (annual_household_income <= smi_eligibility_threshold) {
        let subsidy_eligible_cost = estimated_annual_cost; // For simplicity, assume all care cost is eligible
        let calculated_subsidy = subsidy_eligible_cost * state_data.subsidy_coverage_percent; // State covers a % of cost

        // Apply per-child cap
        const total_subsidy_cap = state_data.subsidy_cap_per_child * total_children_in_care;
        calculated_subsidy = Math.min(calculated_subsidy, total_subsidy_cap);

        // Calculate co-payment
        const income_above_fpl_threshold = annual_household_income - (fpl_income * state_data.co_pay_income_threshold_fpl_multiplier);
        let co_payment = 0;
        if (income_above_fpl_threshold > 0) {
            co_payment = income_above_fpl_threshold * state_data.copay_rate_percent;
        }

        potential_subsidy_amount = Math.max(0, calculated_subsidy - co_payment);
    } else {
        potential_subsidy_amount = 0; // Income too high for subsidy
    }

    // 3. Calculate Net Annual Cost After Subsidy
    let net_annual_cost_after_subsidy = estimated_annual_cost - potential_subsidy_amount;
    net_annual_cost_after_subsidy = Math.max(0, net_annual_cost_after_subsidy); // Cost cannot be negative

    // 4. Calculate Net Cost as % of Income
    let cost_as_percent_of_income = 0;
    if (annual_household_income > 0) {
        cost_as_percent_of_income = (net_annual_cost_after_subsidy / annual_household_income) * 100;
    }

    return {
        estimated_annual_cost: estimated_annual_cost,
        potential_subsidy_amount: potential_subsidy_amount,
        net_annual_cost_after_subsidy: net_annual_cost_after_subsidy,
        cost_as_percent_of_income: cost_as_percent_of_income
    };
}
