const TON_TO_BTUH = 12000; const KW_TO_BTUH = 3412.14;

let q_btuh;
if (inputs.heatLoadUnit === 'Tons') {
    q_btuh = inputs.heatLoad * TON_TO_BTUH;
} else if (inputs.heatLoadUnit === 'kW') {
    q_btuh = inputs.heatLoad * KW_TO_BTUH;
} else { // Default to BTUh if not specified or unrecognized or 'BTUh'
    q_btuh = inputs.heatLoad;
}

const specificHeat = inputs.specificHeatCapacity || 1.0; // BTU/(lb·°F)
const density = inputs.waterDensity || 8.34; // lb/gal
const MIN_VALID_HEAT_LOAD = 0.1; // BTUh
const MIN_VALID_DELTA_T = 0.1; // °F
const MIN_VALID_FLOW_RATE = 0.1; // GPM

let calculatedFlowRate = 0;
let calculatedDeltaT = 0;
let outletWaterTemp = inputs.inletWaterTemp; // Default to inlet temp if no change

// Factor for Q = GPM * deltaT * factor when Q is in BTUh
// factor = density (lb/gal) * specificHeat (BTU/lb°F) * 60 (min/hr)
const factor = density * specificHeat * 60;

// Basic validation for core parameters
if (q_btuh < MIN_VALID_HEAT_LOAD || factor <= 0 || inputs.inletWaterTemp === null || inputs.inletWaterTemp === undefined) {
  return {
    calculatedFlowRate: 0,
    calculatedDeltaT: 0,
    outletWaterTemp: inputs.inletWaterTemp
  };
}

// Determine which value to calculate based on 'knownValueType'
if (inputs.knownValueType === 'Flow Rate') {
    if (inputs.flowRateInput >= MIN_VALID_FLOW_RATE) {
        calculatedFlowRate = inputs.flowRateInput;
        calculatedDeltaT = q_btuh / (calculatedFlowRate * factor);
        calculatedDeltaT = Math.max(MIN_VALID_DELTA_T, calculatedDeltaT); // Ensure deltaT is not zero/negative due to floating point or tiny heat load
        calculatedDeltaT = Math.min(calculatedDeltaT, 50); // Cap deltaT to a reasonable max for practical systems
    } else {
        // Invalid flow rate input, return defaults
        return { 
            calculatedFlowRate: 0, 
            calculatedDeltaT: 0, 
            outletWaterTemp: inputs.inletWaterTemp 
        };
    }
} else if (inputs.knownValueType === 'Delta-T') {
    if (inputs.deltaTInput >= MIN_VALID_DELTA_T) {
        calculatedDeltaT = inputs.deltaTInput;
        calculatedFlowRate = q_btuh / (calculatedDeltaT * factor);
        calculatedFlowRate = Math.max(MIN_VALID_FLOW_RATE, calculatedFlowRate); // Ensure flow rate is not zero/negative
        calculatedFlowRate = Math.min(calculatedFlowRate, 10000); // Cap flow rate to a reasonable max
    } else {
        // Invalid delta-T input, return defaults
        return { 
            calculatedFlowRate: 0, 
            calculatedDeltaT: 0, 
            outletWaterTemp: inputs.inletWaterTemp 
        };
    }
} else {
    // This case should ideally not be reached with a dropdown for knownValueType
    return { 
        calculatedFlowRate: 0, 
        calculatedDeltaT: 0, 
        outletWaterTemp: inputs.inletWaterTemp 
    };
}

outletWaterTemp = inputs.inletWaterTemp + calculatedDeltaT;

return {
  calculatedFlowRate: calculatedFlowRate,
  calculatedDeltaT: calculatedDeltaT,
  outletWaterTemp: outletWaterTemp
};
