function calculate({ initialDiagnosisCost, monthlyMedicationCost, monthlyAppointmentCost, monthlyTherapyCost, annualInsurancePremium, annualInflationRate, projectedYears }) {
  let totalNominalCost = initialDiagnosisCost;
  let totalInflationAdjustedCost = initialDiagnosisCost;

  const annualBaseOperationalCost = (monthlyMedicationCost + monthlyAppointmentCost + monthlyTherapyCost) * 12;
  const annualFixedCost = annualInsurancePremium;
  const inflationRateDecimal = annualInflationRate / 100;

  let currentYearOperationalCost = annualBaseOperationalCost;

  for (let i = 1; i <= projectedYears; i++) {
    // Costs for this specific year without inflation applied to future years' base costs
    const costThisYear_Nominal = annualBaseOperationalCost + annualFixedCost;
    totalNominalCost += costThisYear_Nominal;

    // Costs for this specific year with inflation applied year-over-year
    const costThisYear_Adjusted = currentYearOperationalCost + annualFixedCost;
    totalInflationAdjustedCost += costThisYear_Adjusted;

    // Inflate operational costs for the next year
    currentYearOperationalCost *= (1 + inflationRateDecimal);
  }

  // Calculate average annual cost based on inflation-adjusted figures, excluding initial one-time cost from the average.
  const averageAnnualCost = projectedYears > 0 
    ? (totalInflationAdjustedCost - initialDiagnosisCost) / projectedYears 
    : initialDiagnosisCost;

  return {
    totalNominalCost: totalNominalCost,
    totalInflationAdjustedCost: totalInflationAdjustedCost,
    averageAnnualCost: averageAnnualCost
  };
}
